import axios from 'axios';
const productsUrl = 'http://localhost:9001/product';

export const deleteProduct = async (id) => {
    return await axios.delete(`${productsUrl}/removeProd/${id}`);
}

export const changeproductStatus = async (id) => {
    return await axios.put(`${productsUrl}/changeStatus/${id}`);
}

export const getProducts = async (sortingparams, searchparams) => {
    return await axios.get(`${productsUrl}/getAllProduct${sortingparams}${searchparams}`);
}

export const getsingleProduct = async (prodID) => {
    return await axios.get(`${productsUrl}/singleProduct/${prodID}`);
}

export const addProduct = async (prod) => {
    return await axios.post(`${productsUrl}/addProduct`, prod);
}

export const editsingleProduct = async (userID, prod) => {
    return await axios.put(`${productsUrl}/editProduct/${userID}`, prod)
}

export const addImages = async (formdata)=> {
    return await axios.post(`${productsUrl}/addimage`,  formdata)
}

export const getImages = async (prodid)=> {
    return await axios.get(`${productsUrl}/getImageList/${prodid}`)
}

export const removePic = async (prodid, picid)=> {
    return await axios.delete(`${productsUrl}/picRemove/${prodid}/${picid}`)
}
