import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom';
//import axios from 'axios';
import useValidator from './useValidator'
import {Form} from 'react-bootstrap'
import { addCategory, editsingleCategory, getsingleCategory, getCategoryList } from '../Services/categoryapi'
const AddCategory = () => {
    let myButton = '';
    let { catID } = useParams();
    const history = useHistory();
    const [validator, showValidationMessage] = useValidator()
    const [subcatdata, setSubcatdata] = useState([]);
    const [category, setCategory] = useState({
        name: "",
        parentID: "",
        dp: ""
    })
console.log(category);
    const handlePhoto = (e) => {
        alert("AAnkit")
        console.log(e.target.files[0]);
        setCategory({...category, dp: e.target.files[0]});
    }
    // Getting the Subcategory dropdown data
    useEffect(() => {
        const getSubcatdataList = async () => {
            let res = await getCategoryList();
            console.log(res.data.catdata)
            setSubcatdata(res.data.catdata)
        }
        getSubcatdataList()
    }, [])

    useEffect(() => {
        async function getcatdata() {
            if (catID && catID !== undefined) {
                //axios.get("http://localhost:9001/user/userdata/" + catID).then((res) => {
                let res = await getsingleCategory(catID);
                setCategory({
                    name: res.data.catdata.name,
                    parentID: res.data.catdata.parentID,
                    photo: res.data.catdata.image
                })
            }
        }
        getcatdata()
    }, [catID])

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setCategory({
            ...category,
            [name]: value
        })
    }
    const adddCategory = async () => {
        if (validator.allValid()) {
            const formData = new FormData();
            formData.append('file', category.name)
            formData.append('file', category.parentID)
            formData.append('file', category.dp)

            fetch("http://localhost:9001/category/addCat",{
                method: 'POST',
                body: formData
            }).then((res)=>res.json).then((data)=>{
                console.log(data)
            })

            /*let response = await addCategory(formData);
            if (response.data.message === 'Category Name already exist. Please enter another Category Name!') {
                alert(response.data.message);
            } else {
                alert(response.data.message);
                history.push("/category");
            }*/
        } else {
            showValidationMessage(true);
        }
    }
    //axios.put(`http://localhost:9001/user/editUser/${catID}`, user).then((res) => { })
    const updateCategory = async () => {
        if (validator.allValid()) {
            let response = await editsingleCategory(catID, category);
            if (response.data.message === 'Category updated successfully') {
                alert(response.data.message);
                history.push("/category");
            } else {
                alert(response.data.message);
            }
        } else {
            showValidationMessage(true);
        }
    }
    if (catID && catID !== undefined) {
        myButton = <div className="mybutton" onClick={() => { updateCategory() }}>Update Category</div>
    } else {
        myButton = <div className="mybutton" onClick={() => { adddCategory() }}>Add Category</div>
    }
    return (
        <>
            {/* onChange={(e)=>{setCategory({...category, f_name: e.target.value})}} */}
            <div className="centerform">
                <div className="register">
                    <h1>{catID && catID !== undefined ? "Update Category" : "Add Category"}</h1>
                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <div style={{ float: 'left', width: '100%' }}>
                            <input className="myinputclass" type="text" value={category.name} placeholder="Category Name" name="name" onChange={inputVal} />
                            {validator.message('name', category.name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "Category Name is required"
                                }
                            })}
                        </div>
                    </div>
                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <select style={{ width: '91%' }} className="myinputclass" name="parentID" value={category.parentID} onChange={inputVal}>
                            <option value="">
                                Please Select Subcategory
                            </option>
                            {
                                subcatdata.map((subcatnamedata, index) => 
                                    
                                    <option value={subcatnamedata._id}>
                                        {/*subcatnamedata.subcatname.parentname==='' ? subcatnamedata.name :subcatnamedata.subcatname.parentname*/}
                                        {subcatnamedata.name}
                                    </option>
                                    
                                )
                            }
                        </select>
                    </div>
                    <div style={{ width: '100%', minHeight: '76px' }}>
                    

<Form.Group controlId="formFile" className="mb-3">
    <Form.Label>Category Image: &nbsp;<br/>(Accept only .png, .jpg, .jpeg)&nbsp;&nbsp;</Form.Label>
    <Form.Control type="file" name="myFile" onChange={handlePhoto} accept=".png, .jpg, .jpeg" />
  </Form.Group>
            </div>
            
                    <div style={{ width: '100%', marginTop: '3%' }}>
                        {myButton}
                    </div>
                    <div>OR</div>
                    <div className="button" onClick={() => { history.push("/category") }}>Back </div>
                    {/*<div>OR</div>
                    <div className="button" onClick={() => { history.push("/login") }}>Login </div>*/}
                    {/* OR <Link to="/login">Login</Link> */}
                </div>
            </div>
        </>
    )
}
export default AddCategory;
