import React, { useState, useEffect } from 'react'
import { useHistory, Link } from 'react-router-dom';
import axios from 'axios';
import { Button, Spinner } from 'react-bootstrap'
import SimpleReactValidator from 'simple-react-validator';
const Login = (props) => {
    const history = useHistory();
    const validator = new SimpleReactValidator();
    const [user, setUser] = useState({
        email: "",
        password: "",
        loading: false
    })

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (storedData._id && storedData._id !== "") {
            history.push("/");
        } else {
            history.push("/login");
        }
    }, [])  // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    const loginUser = () => {
        if (this.validator.allValid()) {
            //const {email, password} = user
            setUser({ ...user, loading: true })
            axios.post("http://localhost:9001/user/login", user).then((res) => {
                if (res.data.message === "Login Successfully") {
                    alert(res.data.message)
                    props.updateUserinlocalStorage(res.data.user);
                    history.push("/");
                } else {
                    alert(res.data.message)
                }
                setUser({ ...user, loading: false })
            })
        } else {
            validator.showMessages();
            //forceUpdate();
        }
    }
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="outsider">
                <div className="login">
                    <h2>Login</h2>
                    <input type="text" value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />
                    {/**********   This is where the magic happens     ***********/}
                    {this.validator.message('email', user.email, 'required|email', { className: 'text-danger' })}

                    <input type="password" value={user.password} placeholder="Password" name="password" onChange={inputVal} />
                    {this.validator.message('password', user.password, 'required|min:6|max:12')}


                    {!user.loading ? <Button onClick={() => { loginUser() }}>Login</Button> : <Button variant="primary" disabled>
                        <Spinner
                            as="span"
                            animation="grow"
                            size="sm"
                            role="status"
                            aria-hidden="true"
                        />
                        Loading...
                    </Button>}
                    <br /><br />
                    Forgot Password <Link to="/forgot">Click Here</Link>
                    {/* <div>OR</div>
                    <div className="button" onClick={() => { history.push("/register") }}>Register  OR <Link to="/register">Register</Link> </div>
                    */}
                </div>
            </div>
        </>
    )
}
export default Login;
