import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom';
//import axios from 'axios';
import useValidator from './useValidator'
import {deleteCategory, changecategoryStatus, getCategories, getSubcategoryName} from '../Services/categoryapi'
const AddCategory = () => {
    let myButton = '';
    let { userID } = useParams();
    const history = useHistory();
    const [validator, showValidationMessage] = useValidator()
    const [user, setUser] = useState({
        f_name: "",
        l_name: "",
        email: "",
        phone: "",
        usertype: "",
        password: "",
        confPassword: ""
    })

    useEffect(() => {
        async function getusedata() {
            if (userID && userID !== undefined) {
                //axios.get("http://localhost:9001/user/userdata/" + userID).then((res) => {
                let res = await getsingleUsers(userID);
                setUser({
                    f_name: res.data.userdata.first_name,
                    l_name: res.data.userdata.last_name,
                    email: res.data.userdata.email,
                    phone: res.data.userdata.phone,
                    usertype: res.data.userdata.user_type,
                    password: "",
                    confPassword: ""
                })
            }
        }
        getusedata()
    }, [userID])

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    const addUser = async () => {
        if (validator.allValid()) {
            let response = await addUsers(user);
            if (response.data.message === 'User already registerd. Please use another email!') {
                alert(response.data.message);
            } else {
                alert(response.data.message);
                history.push("/users");
            }
        } else {
            showValidationMessage(true);
        }
    }
    //axios.put(`http://localhost:9001/user/editUser/${userID}`, user).then((res) => { })
    const updateUser = async () => {
        if (validator.allValid()) {
            let response = await editUsers(userID, user);
            if (response.data.message === 'user updated successfully') {
                alert(response.data.message);
                history.push("/users");
            }else{
                alert(response.data.message);
            }
        } else {
            showValidationMessage(true);
        }
    }
    if (userID && userID !== undefined) {
        myButton = <div className="mybutton" onClick={() => { updateUser() }}>Update Category</div>
    } else {
        myButton = <div className="mybutton" onClick={() => { addUser() }}>Add Category</div>
    }
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="centerform">
                <div className="register">
                    <h1>{userID && userID !== undefined ? "Update Category" : "Add Category"}</h1>

                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <div style={{ float: 'left', width: '100%' }}>
                            <input className="myinputclass" type="text" value={user.f_name} placeholder="Category Name" name="f_name" onChange={inputVal} />
                            {validator.message('f_name', user.f_name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "First Name is required"
                                }
                            })}
                        </div>
                    </div>
                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <select style={{ width: '91%' }} className="myinputclass" name="usertype" value={user.usertype} onChange={inputVal}>
                            <option value="">
                                Please Select User Type
                            </option>
                            <option value="Super Admin">
                                Super Admin
                            </option>
                            <option value="Admin">
                                Admin
                            </option>
                            <option value="Staff">
                                Staff
                            </option>
                            <option value="Account">
                                Account
                            </option>
                        </select>
                        {validator.message('usertype', user.usertype, 'required', {
                            className: 'text-danger mywidth',
                            messages: {
                                required: "User Type is required"
                            }
                        })}
                    </div>
                    <div style={{ width: '100%', marginTop: '3%' }}>
                        {myButton}
                    </div>
                    <div>OR</div>
                    <div className="button" onClick={() => { history.push("/category") }}>Back </div>
                    {/*<div>OR</div>
                    <div className="button" onClick={() => { history.push("/login") }}>Login </div>*/}
                    {/* OR <Link to="/login">Login</Link> */}
                </div>
            </div>
        </>
    )
}
export default AddCategory;
