import React, { useState, useEffect } from 'react'
import { useHistory, Link } from 'react-router-dom';
//import axios from 'axios';
import { Button, Spinner } from 'react-bootstrap'
import useValidator from './useValidator'
import { forgotPassword } from '../Services/userapi'
const Forgotpass = (props) => {
    const history = useHistory();
    const [validator, showValidationMessage] = useValidator()
    const [user, setUser] = useState({
        email: "",
        loading: false
    })

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (storedData._id && storedData._id !== "") {
            history.push("/");
        } else {
            history.push("/forgot");
        }
    }, [])  // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    //axios.post("http://localhost:9001/user/forgot", user).then((res) => { })
    const forgotPassword = async () => {
        if (validator.allValid()) {
            setUser({ ...user, loading: true })
            let res = await forgotPassword(user);
            if (res.data.message === "New Password sent to your registered email address. Please check your email.") {
                alert(res.data.message)
                setUser({ ...user, loading: false })
                history.push("/login");
            } else {
                alert(res.data.message)
            }
        } else {
            showValidationMessage(true);
        }
    }
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="outsider">
                <div className="login">
                    <h2>Forgot Password</h2>
                    <input type="text" value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />

                    {/**********   This is where the magic happens     ***********/}
                    {validator.message('email', user.email, 'required|email', {
                        className: 'text-danger mywidth',
                        messages: {
                            required: "Email is required"
                        }
                    })}

                    {!user.loading ? <Button style={{ marginTop: '10px' }} onClick={() => { forgotPassword() }}>Submit</Button> : <Button variant="primary" disabled>
                        <Spinner
                            as="span"
                            animation="grow"
                            size="sm"
                            role="status"
                            aria-hidden="true"
                        />
                        Loading...
                    </Button>}
                    <br /><br /><Link to="/login">Back to Login</Link>
                    {/* <div>OR</div>
                    <div className="button" onClick={() => { history.push("/register") }}>Register  OR <Link to="/register">Register</Link> </div>
                    */}
                </div>
            </div>
        </>
    )
}
export default Forgotpass;
