import { useEffect, useState } from "react"
import { getsingleProduct } from '../Services/productapi'
import {Link} from 'react-router-dom'
const Productimages = (props) => {
    const [productpic, setProductpic] = useState([]);
    useEffect(()=>{
        async function imagesDisplay(){
            if(props.prodID && props.prodID!=='' && props.prodID!==undefined){
                let response = await getsingleProduct(props.prodID);
                //console.log("All Res", response.data.productdata);
                setProductpic(response.data.productdata)
            }
        }
        imagesDisplay()
    }, [props.prodID])

    const removeImage = async (productid, imageid) => {
        alert(productid);
        alert(imageid);
        const response = await removePic(productid, imageid);
        console.log(response);
    }

    //console.log("Final Response", productpic);
    return (
        <div style={{overflow:'hidden', height:'1%'}}>
            {
                props.prodID ?
                    props.product.imageuploaded && props.product.imageuploaded === 'true' ?
                        "" :
                        productpic.productPictures && productpic.productPictures.length > 0 ?
                        productpic.productPictures.map(option =>
                                <div className="image-area" key={option._id}>
                                    <img alt="" src={process.env.React_App_PUBLIC_URL_PRODUCTS + `${option.img}`} style={{ width: '125px', margin: '5px' }} />
                                    <Link to="#" onClick={()=>{removeImage(props.prodID, option._id)}} className="remove-image" style={{display:'inline', cursor:'pointer'}}>&#215;</Link>
                                </div>
                            )
                            : ""
                    : ""
            }
        </div>
    )
}
export default Productimages
