import './App.css';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Page_not_found_404 from './components/Page_not_found_404';
import { Switch, Route } from 'react-router-dom';
import { useEffect, useState } from 'react';
import Topnav from './components/Topnav';
import Footer from './components/Footer';
import Users from './components/Users'
function App() {
  const [logUser, setLogUser] = useState({});
  //const history = useHistory();
  useEffect(() => {
    setLogUser(JSON.parse(localStorage.getItem("MyUser")));
  }, [])
  const updateUserinlocalStorage = (user) => {
    localStorage.setItem("MyUser", JSON.stringify(user));
    setLogUser(user);
  }

  return (
    <div className="App">
      {
            logUser && logUser._id ? <Topnav loggedinuser ={logUser} updateUserinlocalStorage={updateUserinlocalStorage} /> : '' }
      <Switch>
      <Route path="/" exact={true}>
          {
            logUser && logUser._id ? <Dashboard updateUserinlocalStorage={updateUserinlocalStorage} loggedinuser ={logUser} /> : <Login updateUserinlocalStorage={updateUserinlocalStorage} />
          }
        </Route>
        <Route path="/register"><Register /></Route>
        <Route path="/login"><Login updateUserinlocalStorage={updateUserinlocalStorage} /></Route>
        <Route path="/users"><Users loggedinuser ={logUser} /></Route>
        <Route path="*"><Page_not_found_404 /></Route>
      </Switch>
      {
            logUser && logUser._id ? <Footer /> : '' }
    </div>
  );
}
export default App;
