import './App.css';
import React, { useEffect, useState } from 'react';
import { Switch, Route, useHistory } from 'react-router-dom';

import Topnav from './components/Topnav';
import Footer from './components/Footer';
import Dashboard from './components/Dashboard';
import Users from './components/Users';
import Register from './components/Register';
import Login from './components/Login';
import Forgotpass from './components/Forgotpass';
import Categories from './components/Categories';
import AddCategory from './components/AddCategory';
import Products from './components/Products';
import AddProduct from './components/AddProduct';
import PagenotFound404 from './components/PagenotFound404';
import Solicitation from './components/Solicitation';
import Solsearchresult from './components/Solsearchresult'
//import PrivateRoute from './HOC/PrivateRoute'

function App() {
  const [logUser, setLogUser] = useState({});
  //const [userid, setUserid] = useState("");
  const history = useHistory();
  useEffect(() => {
    setLogUser(JSON.parse(localStorage.getItem("MyUser")));
  }, [])
  const updateUserinlocalStorage = (user) => {
    localStorage.setItem("MyUser", JSON.stringify(user));
    setLogUser(user);
  }
  const logout = () => {
    //updateUserinlocalStorage({});
    localStorage.clear();
    history.push("/");
  }
  const storedData = JSON.parse(localStorage.getItem("MyUser"));
if (!storedData){
  console.log('Local storage is empty');
}else{
  console.log('Local storage is not empty');
}

  return (
    <div className="App">
      {storedData ? <Topnav loggedinuser ={logUser} logout={logout} /> : '' }
      <Switch>
      <Route path="/" exact={true}>
          {
            storedData ? <Dashboard logout={logout} loggedinuser ={logUser} /> : <Login updateUserinlocalStorage={updateUserinlocalStorage} />
          }
        </Route>
        <Route path="/users"><Users /></Route>
        <Route path="/register"><Register loggedinuser ={logUser} /></Route>
        <Route path="/login"><Login updateUserinlocalStorage={updateUserinlocalStorage} /></Route>
        <Route path="/updateUser/:userID"><Register loggedinuser ={logUser} /></Route>
        <Route path="/updpro/:userID"><Register loggedinuser ={logUser} /></Route>
        <Route path="/forgot"><Forgotpass /></Route>

        <Route path="/category"><Categories /></Route>
        <Route path="/addCategory"><AddCategory loggedinuser ={logUser} /></Route>
        {/* <PrivateRoute path="/addCategory" component={AddCategory} loggedinuser ={logUser} /> */}
        <Route path="/updateCategory/:catID"><AddCategory loggedinuser ={logUser} /></Route>

        <Route path="/products"><Products /></Route>
        <Route path="/addProducts"><AddProduct loggedinuser ={logUser} /></Route>
        <Route path="/updateProduct/:prodID"><AddProduct loggedinuser ={logUser} /></Route>

        <Route path="/solicitation"><Solicitation searchoption={true} searchimage="images/up_arrow.png" /></Route>
        <Route path="/solresult"><Solsearchresult searchoption={false} searchimage="images/down_arrow.png" /></Route>

        {/*<Route path="/updpro"><UpdateProfile loggedinuser ={logUser} /></Route>
        <Route path="/updpass"><UpdatePassword loggedinuser ={logUser} /></Route>*/}
        <Route path="*"><PagenotFound404 /></Route>
      </Switch>
      {
            storedData ? <Footer /> : '' }
    </div>
  );
}
export default App;
