import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
const User = () => {
    const history = useHistory();
    const [userdata, setUserdata] = useState([]);
    useEffect(() => {
        axios.get("http://localhost:9001/user/userList").then((res) => {
            setUserdata(res.data.userdata);
        })
    }, [])
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    return (
        <Container className="maindivmargin">

            <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>

            <div className="userlist">
                <h2>Users List</h2>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>User Type</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            userdata.map((userdata, i) =>
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{userdata.first_name}</td>
                                    <td>{userdata.last_name}</td>
                                    <td>{userdata.email}</td>
                                    <td>{userdata.phone}</td>
                                    <td>{userdata.user_type}</td>
                                    <td>{userdata.status ? "Active" : "Inactive"}</td>
                                    <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                        <Dropdown.Item onClick={() => { history.push({
                                        pathname: '/register',
                                        search: userdata._id
                                    }) }}>Edit User</Dropdown.Item>
                                        <Dropdown.Item eventKey="1">Delete User</Dropdown.Item>
                                        <Dropdown.Item eventKey="1">Change Status</Dropdown.Item>
                                    </DropdownButton></td>

                                    

                                </tr>
                            )
                        }
                    </tbody>
                </Table>
            </div>
        </Container>
    )
}
export default User
