import { Container } from 'react-bootstrap'
import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'
import SolicitationSearchBar from './SolicitationSearchBar'
const Solicitation = (props) => {
    const history = useHistory();

    const [formstate, setFormstate] = useState({
        status: props.searchoption,
        statustext: "props.searchimage"
    })
    var myval;
    var text;
    if (formstate.status === true) {
        myval = false;
        text = "images/down_arrow.png";
    } else {
        myval = true;
        text = "images/up_arrow.png";
    }

    // Check if localstorage is set or not. If not set than redirect to the home page
    useEffect(() => {
        document.title = 'Solicitation';
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps

    return (
        <>
            <Container className="maindivmargin">
                <div style={{ height: '40px', marginTop: '-4px', marginBottom:'2px' }}>
                    <div style={{ float: 'left', marginLeft: '4px' }}>
                        <h3>Solicitation Search</h3>
                    </div>
                    <div style={{ float: 'right' }}>
                        <img src={formstate.statustext} alt="Open/Close Search Form" style={{ width: '65px', cursor: 'pointer' }} onClick={() => { setFormstate({ ...formstate, status: myval, statustext: text }) }} />
                    </div>
                </div>
                {formstate.status ? <SolicitationSearchBar /> : ""}
            </Container>
        </>
    )
}

export default Solicitation;
