import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
import ReactPaginate from 'react-paginate';
const User = () => {
    const history = useHistory();
    const [userdata, setUserdata] = useState([]);

    const [offset, setOffset] = useState(0);
    //const [data, setData] = useState([]);
    const [perPage] = useState(5);
    const [pageCount, setPageCount] = useState(0)

    useEffect(() => {
        userList()
    }, [offset])

    const handlePageClick = (e) => {
        const selectedPage = e.selected;
        setOffset(selectedPage + 1)
    };

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        //console.log(storedData)
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const removeUser = (user_id, name) => {
        var result = window.confirm("Want to delete the User " + name + "?");
        if (result) {
            axios.delete("http://localhost:9001/user/userdelete/" + user_id).then((res) => {
                if(res.data.message && res.data.message==='user deleted successfully'){
                    alert(res.data.message);
                    userList()
                }
            })
        }
    }
    const statusUser = (user_id) => {
        axios.put("http://localhost:9001/user/changeStatus/" + user_id).then((res) => {
            if(res.data.message && res.data.message==='user status updated successfully'){
                alert(res.data.message);
                userList()
            }
        })
    }
    const userList = () => {
        axios.get("http://localhost:9001/user/userList").then((res) => {
            const data = res.data.userdata;
            const slice = data.slice(offset, offset + perPage)
                const postData = slice.map(pd => <div key={pd._id}>
                   

                   <tr key={pd._id}>
                                    <td>AA</td>
                                    <td>{pd.first_name}</td>
                                    <td>{pd.last_name}</td>
                                    <td>{pd.email}</td>
                                    <td>{pd.phone}</td>
                                    <td>{pd.user_type}</td>
                                    <td>{pd.status ? "Active" : "Inactive"}</td>
                                    <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                        {/*<Dropdown.Item onClick={() => { props.updateUser(pd._id) }}>Edit User</Dropdown.Item>*/}
                                        <Dropdown.Item onClick={()=>{history.push("/updateUser/"+pd._id)}}>Edit User</Dropdown.Item>
                                        {
                                            JSON.parse(localStorage.getItem("MyUser"))._id && JSON.parse(localStorage.getItem("MyUser"))._id!==pd._id ?
                                            <> <Dropdown.Item onClick={() => { removeUser(pd._id, pd.first_name) }}>Delete User</Dropdown.Item>
                                            <Dropdown.Item onClick={() => { statusUser(pd._id) }}>Change Status</Dropdown.Item></>
                                            :
                                            ""
                                        }
                                    </DropdownButton></td>
                                </tr>


                </div>)
                setUserdata(postData)
                setPageCount(Math.ceil(data.length / perPage))

            //setUserdata(res.data.userdata);
        })
    }
    return (
        <Container className="maindivmargin">

            <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>

            <div className="userlist">
                <h2>Users List</h2>
                <ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}/>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>User Type</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {userdata}
                    </tbody>
                </Table>
            </div>
        </Container>
    )
}
export default User
