import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button, Pagination } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
import "./paginationstyle.css";
const User = () => {
    const history = useHistory();
    const [userdata, setUserdata] = useState([]);
    // This stated is used for search query parameter
    const [q, setQ] = useState("");

    // Pagination Code Starts Here
    const [currentPage, setcurrentPage] = useState(1);
    const [itemsPerPage, setitemsPerPage] = useState(5);
    const [pageNumberLimit, setpageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);

    const handleClick = (event) => {
        setcurrentPage(Number(event.target.id));
    };

    const pages = [];
    for (let i = 1; i <= Math.ceil(userdata.length / itemsPerPage); i++) {
        pages.push(i);
    }

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = userdata.slice(indexOfFirstItem, indexOfLastItem);

    const renderPageNumbers = pages.map((number) => {
        if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
            return (
                <li
                    key={number}
                    id={number}
                    onClick={handleClick}
                    className={currentPage === number ? "active" : null}
                >
                    {number}
                </li>
            );
        } else {
            return null;
        }
    });

    const handleNextbtn = () => {
        setcurrentPage(currentPage + 1);

        if (currentPage + 1 > maxPageNumberLimit) {
            setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
        }
    };

    const handlePrevbtn = () => {
        setcurrentPage(currentPage - 1);

        if ((currentPage - 1) % pageNumberLimit === 0) {
            setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
        }
    };

    let pageIncrementBtn = null;
    if (pages.length > maxPageNumberLimit) {
        pageIncrementBtn = <li onClick={handleNextbtn}> &hellip; </li>;
    }

    let pageDecrementBtn = null;
    if (minPageNumberLimit >= 1) {
        pageDecrementBtn = <li onClick={handlePrevbtn}> &hellip; </li>;
    }
    /*
    const handleLoadMore = () => {
        setitemsPerPage(itemsPerPage + 5);
    };
    */
    // Pagination Code Ends Here


    useEffect(() => {
        userList()
    }, [])
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        //console.log(storedData)
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const removeUser = (user_id, name) => {
        var result = window.confirm("Want to delete the User " + name + "?");
        if (result) {
            axios.delete("http://localhost:9001/user/userdelete/" + user_id).then((res) => {
                if (res.data.message && res.data.message === 'user deleted successfully') {
                    alert(res.data.message);
                    userList()
                }
            })
        }
    }
    const statusUser = (user_id) => {
        axios.put("http://localhost:9001/user/changeStatus/" + user_id).then((res) => {
            if (res.data.message && res.data.message === 'user status updated successfully') {
                alert(res.data.message);
                userList()
            }
        })
    }
    const userList = () => {
        axios.get("http://localhost:9001/user/userList").then((res) => {
            setUserdata(res.data.userdata);
        })
    }
    const inputVal = (e) => {
        const { name, value } = e.target;
        setQ({
            ...q,
            [name]: value
        })
    }
    return (
        <Container className="maindivmargin">

<div style={{width:'100%'}}>
    <div style={{width:'60%', float:'left'}}>
        <input style={{width:'40%'}} type="text" placeholder="Search...." name="search" value={q} onChange={inputVal} /> <Button onClick={() => { history.push("/register") }}>Search</Button>
    </div>
            <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>
            </div>
            <div className="userlist">
                <h2>Users List</h2>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>User Type</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            currentItems.map((userdata, i) =>
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{userdata.first_name}</td>
                                    <td>{userdata.last_name}</td>
                                    <td>{userdata.email}</td>
                                    <td>{userdata.phone}</td>
                                    <td>{userdata.user_type}</td>
                                    <td>{userdata.status ? "Active" : "Inactive"}</td>
                                    <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                        {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                                        <Dropdown.Item onClick={() => { history.push("/updateUser/" + userdata._id) }}>Edit User</Dropdown.Item>
                                        {
                                            JSON.parse(localStorage.getItem("MyUser"))._id && JSON.parse(localStorage.getItem("MyUser"))._id !== userdata._id ?
                                                <> <Dropdown.Item onClick={() => { removeUser(userdata._id, userdata.first_name) }}>Delete User</Dropdown.Item>
                                                    <Dropdown.Item onClick={() => { statusUser(userdata._id) }}>Change Status</Dropdown.Item></>
                                                :
                                                ""
                                        }
                                    </DropdownButton></td>
                                </tr>
                            )
                        }
                    </tbody>
                </Table>
            </div>
            <div>
                <div style={{ marginTop: '30px' }}>
                    <ul className="pageNumbers">
                        <li>
                            <button
                                onClick={handlePrevbtn}
                                disabled={currentPage === pages[0] ? true : false}
                            >
                                Prev
                            </button>
                        </li>
                        {pageDecrementBtn}
                        <Pagination size="sm">{renderPageNumbers}</Pagination>
                        {pageIncrementBtn}

                        <li>
                            <button
                                onClick={handleNextbtn}
                                disabled={currentPage === pages[pages.length - 1] ? true : false}
                            >
                                Next
                            </button>
                        </li>
                    </ul>
                    {/*<button onClick={handleLoadMore} className="loadmore">
                    Load More
                </button>*/}
                </div>
            </div>
        </Container>

    )
}
export default User
