import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom';
import useValidator from './useValidator'
import { Form } from 'react-bootstrap'
import { getCategoryList } from '../Services/categoryapi'
import { addCategory, editsingleCategory, getsingleProduct } from '../Services/productapi'
const AddProduct = () => {
    let myButton = '';
    let { prodID } = useParams();
    const history = useHistory();
    const [validator, showValidationMessage] = useValidator()
    const [subcatdata, setSubcatdata] = useState([]);
    const [product, setProduct] = useState({
        name: "",
        quantity: "",
        price: "",
        categoryID: "",
        discount: "",
        description: "",
        productPictures: ""
    })
    // Getting the Subcategory dropdown data
    useEffect(() => {
        const getSubcatdataList = async () => {
            let res = await getCategoryList();
            setSubcatdata(res.data.catdata)
        }
        getSubcatdataList()
    }, [])

    const handlePhoto = (e) => {
        setProduct({ ...product, productPicture: e.target.files });
    }
    
    //console.log("list data", subcatdata)
    useEffect(() => {
        async function getproddata() {
            if (prodID && prodID !== undefined) {
                //axios.get("http://localhost:9001/user/userdata/" + catID).then((res) => {
                let res = await getsingleProduct(prodID);
                setProduct({
                    name: res.data.productdata.name,
                    quantity: res.data.productdata.quantity,
                    price: res.data.productdata.price,
                    categoryID: res.data.productdata.categoryID,
                    discount: res.data.productdata.discount,
                    description: res.data.productdata.description,
                    productPictures: res.data.productdata.productPictures
                })
            }
        }
        getproddata()
    }, [prodID])

    console.log(product);

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setProduct({
            ...product,
            [name]: value
        })
    }
    const adddProduct = async () => {
        if (validator.allValid()) {
            const formData = new FormData();
            formData.append('name', product.name)
            formData.append('parentID', product.parentID)
            formData.append('dp', product.dp)
            let response = await addCategory(formData);
            if (response.data.message === 'Category Name already exist. Please enter another Category Name!') {
                alert(response.data.message);
            } else {
                alert(response.data.message);
                history.push("/products");
            }
        } else {
            showValidationMessage(true);
        }
    }
    //axios.put(`http://localhost:9001/user/editUser/${catID}`, user).then((res) => { })
    const updateProduct = async () => {
        if (validator.allValid()) {
            const formData = new FormData();
            formData.append('name', product.name)
            formData.append('parentID', product.parentID)
            formData.append('dp', product.dp)
            let response = await editsingleCategory(catID, formData);
            if (response.data.message === 'Category updated successfully') {
                alert(response.data.message);
                history.push("/products");
            } else {
                alert(response.data.message);
            }
        } else {
            showValidationMessage(true);
        }
    }
    if (prodID && prodID !== undefined) {
        myButton = <div className="mybutton" onClick={() => { updateProduct() }}>Update Product</div>
    } else {
        myButton = <div className="mybutton" onClick={() => { adddProduct() }}>Add Product</div>
    }

    const createCategoryList = (categories, options = [], sub_mark = '') => {
        for (let category of categories) {
            options.push({ value:  category._id, name: sub_mark+category.name, children: category.parentID});
            if(category.children.length > 0){
                createCategoryList(category.children, options, sub_mark+'---')
            }
        }
        return options;
    }
    return (
        <>
            {/* onChange={(e)=>{setProduct({...product, f_name: e.target.value})}} */}
            <div className="centerform">
                <div className="register">
                    <h1>{catID && catID !== undefined ? "Update Product" : "Add Product"}</h1>
                                        
                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <div style={{ float: 'left', width: '50%' }}>
                            <input className="myinputclass" type="text" value={user.f_name} placeholder="First Name" name="f_name" onChange={inputVal} />
                            {validator.message('f_name', user.f_name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "First Name is required"
                                }
                            })}
                        </div>
                        <div style={{ float: 'right', width: '50%' }}>
                            <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" value={user.l_name} placeholder="Last Name" name="l_name" onChange={inputVal} />
                            {validator.message('l_name', user.l_name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "Last Name is required"
                                }
                            })}
                        </div>
                    </div>


                    <div style={{ width: '100%', minHeight: '76px' }}>
                        {/*<select style={{ width: '91%' }} className="myinputclass" name="parentID" value={product.parentID} onChange={inputVal}>
                            <option value="">
                                Please Select Subcategory
                            </option>
                            {
                                subcatdata.map((subcatnamedata, index) =>
                                    <option value={subcatnamedata._id}>
                                        {subcatnamedata.name}
                                        {subcatnamedata.children.map((subcatchild, i) =>
                                            "\n" + '--' + subcatchild.name
                                        )}
                                    </option>
                                )
                            }
                        </select>*/}


                        <select style={{ width: '91%' }} className="myinputclass" name="parentID" value={product.parentID} onChange={inputVal}>
                        <option value="">Please Select Subcategory</option>
                            {
                                createCategoryList(subcatdata).map(option=>
                                <option key={option.value} value={option.value}>{option.name}</option>)
                            }
                        </select>
                        
                    </div>
                    <div style={{ width: '100%', minHeight: '76px' }}>


                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Label>Category Image: &nbsp;<br />(Accept only .png, .jpg, .jpeg)&nbsp;&nbsp;</Form.Label>
                            <Form.Control type="file" name="myFile" onChange={handlePhoto} accept=".png, .jpg, .jpeg" />
                        </Form.Group><br />

                        {catID && catID !== undefined && product.dp && product.dp !== undefined && product.dp !== '[object File]' ? <img alt="" src={process.env.React_App_PUBLIC_URL + `${product.dp}`} style={{ width: '110px' }} /> : ""}

                    </div>

                    <div style={{ width: '100%', marginTop: '3%' }}>
                        {myButton}
                    </div>
                    <div>OR</div>
                    <div className="button" onClick={() => { history.push("/products") }}>Back </div>
                    {/*<div>OR</div>
                    <div className="button" onClick={() => { history.push("/login") }}>Login </div>*/}
                    {/* OR <Link to="/login">Login</Link> */}
                </div>
            </div>
        </>
    )
}
export default AddProduct;
