                            <input type="checkbox" onChange={checkVal} name="statepref[]" value="KS" />&nbsp; Kansas<br />
                            <input type="checkbox" onChange={checkVal} name="statepref[]" value="KY" />&nbsp; Kentucky<br />
                            <input type="checkbox" onChange={checkVal} name="statepref[]" value="LA" />&nbsp; Louisiana<br />
                            <input type="checkbox" onChange={checkVal} name="statepref[]" value="ME" />&nbsp; Maine<br />
                            <input type="checkbox" onChange={checkVal} name="statepref[]" value="MD" />&nbsp; Maryland<br />
                            <input type="checkbox" onChange={checkVal} name="statepref[]" value="MA" />&nbsp; Massachusetts<br />
