                    <input className="myinputclass" type="text" value={searchform.naicstext} placeholder="Enter Keyword or 2-6 digit code" name="naicstext" onChange={naicsupdate} />
                    {
                        naicsgetdata.length > 0 ?
                            <div className="chkbox-area" style={{ height: '200px', width: '100%' }}>
                                {
                                    naicsgetdata.map((datanaics, index) =>
