                            setasidegetdata.map((asidedata, i) =>
                                <div key={i}>
                                    <div style={{ float: 'left' }}>
                                        <input type="checkbox" onChange={checkVal} name="aside[]" value={asidedata.aside_abbr} />
                                    </div>
                                    <div style={{ float: 'left' }}>
                                        &nbsp; {asidedata.aside_name}
                                    </div><br />
                                </div>
