import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button, Pagination, Spinner } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
import Paginations from './Paginations';
//import Protected from './Protected';
import "./paginationstyle.css";
const User = () => {
    const history = useHistory();
    const [userdata, setUserdata] = useState([]);
    const [resultcount, setResultcount] = useState(0);
    const [loading, setLoading] = useState(false);
    // Sorting
    const [sortingcolumn, setSortingcolumn] = useState("first_name");
    const [sortingval, setSortingval] = useState(1);
    // This stated is used for search query parameter
    const [q, setQ] = useState("");

    /*const prevCurrentpageRef = useRef();
    const prevItemperPageRef = useRef();
    useEffect(()=>{
        prevCurrentpageRef.current = currentPage;
        prevItemperPageRef.current = itemsPerPage;
    })
    const prevCurrentPage = prevCurrentpageRef.current;
    const prevItemsperPage = prevItemperPageRef.current;
*/
var currentItems = [];
    function getPagination(datafull){
        currentItems = datafull;
        alert(currentItems);
    }

    
    
    // Pagination Code Ends Here


    useEffect(()=>{
        userList()
    },[sortingval, sortingcolumn])

    // Check if localstorage is set or not. If not set than redirect to the home page
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps

    const removeUser = (user_id, name) => {
        var result = window.confirm("Want to delete the User " + name + "?");
        if (result) {
            axios.delete("http://localhost:9001/user/userdelete/" + user_id).then((res) => {
                if (res.data.message && res.data.message === 'user deleted successfully') {
                    alert(res.data.message);
                    userList()
                }
            })
        }
    }
    const statusUser = (user_id) => {
        axios.put("http://localhost:9001/user/changeStatus/" + user_id).then((res) => {
            if (res.data.message && res.data.message === 'user status updated successfully') {
                alert(res.data.message);
                userList()
            }
        })
    }
    
    const sortingdata = (colname, val)=>{
        setSortingcolumn(colname)
        setSortingval(val)
    }

    

    let sortingparams = '';
    let searchparams = '';
    const userList = () => {
        console.log("userlist function called")
        setLoading(true)
        sortingparams = '/'+sortingcolumn+'/'+sortingval;
        if(q){
            //setcurrentPage(1);
            //setitemsPerPage(5);
            searchparams = '/'+q
        }else{
            searchparams = '';
        }
        axios.get("http://localhost:9001/user/userList"+sortingparams+''+searchparams).then((res) => {
            //console.log(res.data.userdata.length)
            setResultcount(res.data.userdata.length);
            setUserdata(res.data.userdata);
        })
        setLoading(false)
    }
    /*const clearSearch=()=>{
        setQ("")
        alert(q)
        alert("AAAAAA");
        userList()
    }
    */
    
    return (
        <Container className="maindivmargin">

<div style={{width:'100%'}}>
    <div style={{width:'60%', float:'left'}}>
        <input style={{width:'40%'}} type="text" placeholder="Search...." name="search" value={q} onChange={(e)=>{setQ(e.target.value)}} /> <Button onClick={() => { userList() }}>Search</Button> {/*<Button onClick={() => { clearSearch() }}>Clear</Button>*/}
    </div>
            <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>
            </div>

            <div style={{width:'100%'}}>
                <div style={{width:'25%', float:'left', marginLeft:'2%', marginTop:'1%'}}><h2>Users List</h2></div>
                <div style={{width:'60%', float:'right', marginTop: '2%', marginLeft:'2%', marginRight:'2%', textAlign:'right'}}><h5>{resultcount} Results Found</h5></div>
            </div>
            <div className="userlist">
                
<h2>&nbsp;</h2>
                


                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name <button onClick={()=>{sortingdata("first_name","1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬆️</button> <button onClick={()=>{sortingdata("first_name","-1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬇️</button> </th>
                            <th>Last Name <button onClick={()=>{sortingdata("last_name","1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬆️</button> <button onClick={()=>{sortingdata("last_name","-1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬇️</button></th>
                            <th>Email <button onClick={()=>{sortingdata("email","1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬆️</button> <button onClick={()=>{sortingdata("email","-1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬇️</button></th>
                            <th>Phone <button onClick={()=>{sortingdata("phone","1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬆️</button> <button onClick={()=>{sortingdata("phone","-1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬇️</button></th>
                            <th>User Type <button onClick={()=>{sortingdata("user_type","1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬆️</button> <button onClick={()=>{sortingdata("user_type","-1")}} style={{border:'0', backgroundColor:'white', margin:'0', padding:'0'}}>⬇️</button></th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            !loading ?
                            currentItems.map((userdata, i) =>
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{userdata.first_name}</td>
                                    <td>{userdata.last_name}</td>
                                    <td>{userdata.email}</td>
                                    <td>{userdata.phone}</td>
                                    <td>{userdata.user_type}</td>
                                    <td>{userdata.status ? "Active" : "Inactive"}</td>
                                    <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                        {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                                        <Dropdown.Item onClick={() => { history.push("/updateUser/" + userdata._id) }}>Edit User</Dropdown.Item>
                                        {
                                            JSON.parse(localStorage.getItem("MyUser"))._id && JSON.parse(localStorage.getItem("MyUser"))._id !== userdata._id ?
                                                <> <Dropdown.Item onClick={() => { removeUser(userdata._id, userdata.first_name) }}>Delete User</Dropdown.Item>
                                                    <Dropdown.Item onClick={() => { statusUser(userdata._id) }}>Change Status</Dropdown.Item></>
                                                :
                                                ""
                                        }
                                    </DropdownButton></td>
                                </tr>
                            ) : <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                          </Spinner>
                        }
                    </tbody>
                </Table>
            </div>
            <Paginations userdata={userdata} getPagination={getPagination} />
        </Container>

    )
}
export default User
