import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';
import axios from 'axios';
const Login = (props) => {
const history = useHistory();
const [user, setUser] = useState({
    email: "",
    password: ""
})
const inputVal = (e) => {
    const {name, value} = e.target;
    setUser({
        ...user,
        [name]: value
    })
}
const loginUser = () => {
    //const {email, password} = user
