import axios from 'axios';
const usersUrl = 'http://localhost:9001/product';

export const deleteProduct = async (id) => {
    return await axios.delete(`${usersUrl}/removeProd/${id}`);
}

export const changeproductStatus = async (id) => {
    return await axios.put(`${usersUrl}/changeStatus/${id}`);
}

export const getProducts = async (sortingparams, searchparams) => {
    return await axios.get(`${usersUrl}/getAllProduct${sortingparams}${searchparams}`);
}

export const getsingleProduct = async (prodID) => {
    return await axios.get(`${usersUrl}/singleProduct/${prodID}`);
}

export const addProduct = async (prod) => {
    return await axios.post(`${usersUrl}/addProduct`, prod);
}

export const editsingleProduct = async (userID, prod) => {
    return await axios.put(`${usersUrl}/editProduct/${userID}`, prod)
}

export const addImages = async (formdata)=> {
    return await axios.post(`${usersUrl}/addimage`,  formdata)
}

export const getImages = async (prodid)=> {
    return await axios.get(`${usersUrl}/getImageList/${prodid}`)
}
