import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button, Pagination, Spinner } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
//import axios from 'axios';
import {deleteCategory, changecategoryStatus, getCategories} from '../Services/categoryapi'
import "./paginationstyle.css";
const Categories = () => {
    const history = useHistory();
    // Main User list data return State
    const [categorydata, setCategorydata] = useState([]);
    // Total Count State
    const [resultcount, setResultcount] = useState(0);
    // Loading bar State
    const [loading, setLoading] = useState(false);
    // Sorting State
    const [sortingcolumn, setSortingcolumn] = useState("name");
    const [sortingval, setSortingval] = useState(1);
    // This stated is used for search query parameter
    const [q, setQ] = useState("");

    // Pagination Code Starts Here
    const [currentPage, setcurrentPage] = useState(1);
    const [itemsPerPage, setitemsPerPage] = useState(5);
    const [pageNumberLimit, setpageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);
    const handleClick = (event) => {
        setcurrentPage(Number(event.target.id));
    };
    const pages = [];
    for (let i = 1; i <= Math.ceil(categorydata.length / itemsPerPage); i++) {
        pages.push(i);
    }
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = categorydata.slice(indexOfFirstItem, indexOfLastItem);
    const renderPageNumbers = pages.map((number) => {
        if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
            return (
                <li
                    key={number}
                    id={number}
                    onClick={handleClick}
                    className={currentPage === number ? "active" : null}
                >
                    {number}
                </li>
            );
        } else {
            return null;
        }
    });
    const handleNextbtn = () => {
        setcurrentPage(currentPage + 1);
        if (currentPage + 1 > maxPageNumberLimit) {
            setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
        }
    };
    const handlePrevbtn = () => {
        setcurrentPage(currentPage - 1);
        if ((currentPage - 1) % pageNumberLimit === 0) {
            setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
        }
    };
    let pageIncrementBtn = null;
    if (pages.length > maxPageNumberLimit) {
        pageIncrementBtn = <li onClick={handleNextbtn}> &hellip; </li>;
    }
    let pageDecrementBtn = null;
    if (minPageNumberLimit >= 1) {
        pageDecrementBtn = <li onClick={handlePrevbtn}> &hellip; </li>;
    }
    /*
    const handleLoadMore = () => {
        setitemsPerPage(itemsPerPage + 5);
    };
    */
    // Pagination Code Ends Here
    // This useEffect called first time when page load and when sortingval state and sortingcolumn state changed
    useEffect(() => {
        userList()
    }, [sortingval, sortingcolumn]) // eslint-disable-line react-hooks/exhaustive-deps

    // Check if localstorage is set or not. If not set than redirect to the home page
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps

    
    // This funciton is called when remove the user from the list
    const removeUser = async (user_id, name) => {
        var result = window.confirm("Want to delete the Category " + name + "?");
        if (result) {
            let response = await deleteCategory(user_id);
            if (response.data.message && response.data.message === 'Category deleted successfully!') {
                alert(response.data.message);
                userList()
            }
        }
    }
    // This function is called when change the user status
    const statusUser = async (id) => {
        let response = await changecategoryStatus(id);
        if (response.data.message && response.data.message === 'Category status updated successfully') {
            alert(response.data.message);
            userList()
        }
    }
    // This function is used for change the status of sorting
    const sortingdata = (colname, val) => {
        setSortingcolumn(colname)
        setSortingval(val)
    }

    // This function is used for fetch the all user data and in this function we have passed the sorting column and search parameter
    let sortingparams = '';
    let searchparams = '';
    const userList = async () => {
        setLoading(true)
        sortingparams = '/' + sortingcolumn + '/' + sortingval;
        if (q) {
            setcurrentPage(1);
            setitemsPerPage(5);
            searchparams = '/' + q
        } else {
            searchparams = '/';
        }
        let response = await getCategories(sortingparams, searchparams);
        if(response.data.message && response.data.message === 'category list fetched') {
            setResultcount(response.data.catdata.length);
            setCategorydata(response.data.catdata);        
        }
        setLoading(false)
        /*axios.get("http://localhost:9001/user/userlist" + sortingparams + '' + searchparams).then((res) => {
            setResultcount(res.data.categorydata.length);
            setCategorydata(res.data.categorydata);
        })
        setLoading(false)*/
    }
    /*const clearSearch=()=>{
        setQ("")
        alert(q)
        alert("AAAAAA");
        userList()
    }
    */
    return (
        <Container className="maindivmargin">

            <div style={{ width: '100%' }}>
                <div style={{ width: '60%', float: 'left' }}>
                    <input style={{ width: '40%' }} type="text" placeholder="Search...." name="search" value={q} onChange={(e) => { setQ(e.target.value) }} /> <Button onClick={() => { userList() }}>Search</Button> {/*<Button onClick={() => { clearSearch() }}>Clear</Button>*/}
                </div>
                <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/addCategory") }}>Add New Category</Button></div>
            </div>

            <div style={{ width: '100%' }}>
                <div style={{ width: '25%', float: 'left', marginLeft: '2%', marginTop: '1%' }}><h2>Category List</h2></div>
                <div style={{ width: '60%', float: 'right', marginTop: '2%', marginLeft: '2%', marginRight: '2%', textAlign: 'right' }}><h5>{resultcount} Results Found</h5></div>
            </div>
            <div className="userlist">
                <h2>&nbsp;</h2>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Category Name <button onClick={() => { sortingdata("name", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("name", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button> </th>
                            <th>Parent ID {/*<button onClick={() => { sortingdata("last_name", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("last_name", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button>*/}</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            !loading ?
                                currentItems.map((userdata, i) =>
                                    <tr key={i}>
                                        <td>{i + 1}</td>
                                        <td>{userdata.name}</td>
                                        <td>{userdata.parentID===0 ? "N/A":userdata.parentID}</td>
                                        <td>{userdata.status ? "Active" : "Inactive"}</td>
                                        <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                            {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                                            <Dropdown.Item onClick={() => { history.push("/updateCategory/" + userdata._id) }}>Edit User</Dropdown.Item>
                                             <Dropdown.Item onClick={() => { removeUser(userdata._id, userdata.first_name) }}>Delete User</Dropdown.Item>
                                            <Dropdown.Item onClick={() => { statusUser(userdata._id) }}>Change Status</Dropdown.Item>
                                        </DropdownButton></td>
                                    </tr>
                                ) : <tr><td colSpan="5" style={{paddingTop:'20px', paddingBottom:'20px'}}><Spinner animation="border" role="status">
                                    <span className="visually-hidden">Loading...</span>
                                </Spinner></td></tr>
                        }
                    </tbody>
                </Table>
            </div>
            <div>
                <div style={{ marginTop: '30px' }}>
                    <ul className="pageNumbers">
                        <li>
                            <button
                                onClick={handlePrevbtn}
                                disabled={currentPage === pages[0] ? true : false}
                            >
                                Prev
                            </button>
                        </li>
                        {pageDecrementBtn}
                        <Pagination size="sm">{renderPageNumbers}</Pagination>
                        {pageIncrementBtn}

                        <li>
                            <button
                                onClick={handleNextbtn}
                                disabled={currentPage === pages[pages.length - 1] ? true : false}
                            >
                                Next
                            </button>
                        </li>
                    </ul>
                    {/*<button onClick={handleLoadMore} className="loadmore">
                    Load More
                </button>*/}
                </div>
            </div>
        </Container>

    )
}
export default Categories
