const Productimages = (props) => {
    let stylemargin = '';
    if(props.product.productPictureviewonly && props.product.productPictureviewonly.length > 5){
        stylemargin = '270px'
    }else{
        stylemargin = '10px'
    }
    return (
        <div style={{marginBottom:stylemargin}}>
            {
                props.prodID ?
                    props.product.imageuploaded && props.product.imageuploaded === 'true' ?
                        "" :
                        props.product.productPictureviewonly && props.product.productPictureviewonly.length > 0 ?
                            props.product.productPictureviewonly.map(option =>
                                <div className="image-area">
                                    <img key={option._id} alt="" src={process.env.React_App_PUBLIC_URL_PRODUCTS + `${option.img}`} style={{ width: '125px', margin: '5px' }} />
                                    <a className="remove-image" href="#" style={{display:'inline'}}>&#215;</a>
                                </div>
                            )
                            : ""
                    : ""
            }
        </div>
    )
}
export default Productimages
