import React, { useState, useEffect } from 'react'
import { Table } from 'react-bootstrap'
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css"
import { ghUserData } from '../Services/userapi'
import Tasklist from '../components/Tasklist';
import { Button } from 'react-bootstrap'
const Dashboardgh = (props) => {
    let loggedInUserId = "";
    const storedData = JSON.parse(localStorage.getItem("MyUser"));
    if (storedData) {
        loggedInUserId = storedData.user_id;
    }
    const [userlist, setUserlist] = useState([]);
    const [dateseven, setDateseven] = useState([]);
    const [tasklist, setTasklist] = useState([]);
    const [AllUserlist, setAllUserlist] = useState([]);
    const [searchval, setSearchval] = useState({
        start_date: new Date(new Date().getTime() - (6 * 24 * 60 * 60 * 1000)),
        end_date: new Date(),
        resource: "",
        gh_id: loggedInUserId
    })
    //alert(searchval.start_date);
    //alert(searchval.end_date);
    const datconverted = (dated) => {
        var today_date = dated;
        var dd_date = String(today_date.getDate()).padStart(2, '0');
        var mm_date = String(today_date.getMonth() + 1).padStart(2, '0');
        var yyyy_date = today_date.getFullYear();
        today_date = mm_date + '/' + dd_date + '/' + yyyy_date;
        return today_date;
    }
    const getDifferenceInDays = (start_date, end_date) => {
        const diffInMs = Math.abs(end_date - start_date);
        return diffInMs / (1000 * 60 * 60 * 24);
    }
    console.log("Start Date = ", datconverted(searchval.start_date));
    console.log("End Date = ", datconverted(searchval.end_date));
    console.log("Days Difference = ", getDifferenceInDays(new Date(datconverted(searchval.start_date)), new Date(datconverted(searchval.end_date))))

    const datasevendays = () => {
        var no_of_days;
        if ((searchval.start_date && searchval.start_date !== '' && searchval.start_date !== null) && (searchval.end_date && searchval.end_date !== '' && searchval.end_date !== null)) {
            const date1 = new Date(searchval.start_date);
            const date2 = new Date(searchval.end_date);
            const diffTime = Math.abs(date2 - date1);
            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
            //no_of_days = diffDays;
            no_of_days = getDifferenceInDays(new Date(datconverted(searchval.start_date)), new Date(datconverted(searchval.end_date)));
        } else if ((searchval.start_date && searchval.start_date !== '' && searchval.start_date !== null) && (searchval.end_date === '' || searchval.end_date === null)) {
            const date1 = new Date(searchval.start_date);
            const date2 = new Date();
            const diffTime = Math.abs(date2 - date1);
            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
            no_of_days = diffDays;
        } else if ((searchval.start_date === '' || searchval.start_date === null) && (searchval.end_date && searchval.end_date !== '' && searchval.end_date !== null)) {
            const date1 = new Date(searchval.end_date.getTime() - (30 * 24 * 60 * 60 * 1000));
            const date2 = new Date(searchval.end_date);
            const diffTime = Math.abs(date2 - date1);
            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
            no_of_days = diffDays;
        } else {
            no_of_days = '8'
        }
        alert(no_of_days);

        var today_date = new Date();
        var dd_date = String(today_date.getDate()).padStart(2, '0');
        var mm_date = String(today_date.getMonth() + 1).padStart(2, '0');
        var yyyy_date = today_date.getFullYear();
        today_date = mm_date + '/' + dd_date + '/' + yyyy_date;

        var today;
        if (searchval.end_date && searchval.end_date !== '' && searchval.end_date !== null) {
            today = new Date(searchval.end_date);
        } else {
            today = new Date(today_date);
        }
        //alert(today);
        //var today = new Date();
        var dd = String(today.getDate()).padStart(2, '0');
        var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        var yyyy = today.getFullYear();
        today = mm + '/' + dd + '/' + yyyy;
        var result = [];
        for (var i = 0; i < no_of_days; i++) {
            var d = new Date(today);
            d.setDate(d.getDate() - i);
            dd = d.getDate();
            mm = d.getMonth() + 1;
            yyyy = d.getFullYear();
            if (dd < 10) { dd = '0' + dd }
            if (mm < 10) { mm = '0' + mm }
            var datefinal = yyyy + '-' + mm + '-' + dd;
            //alert(datefinal)
            result.push(datefinal)
        }
        setDateseven(result);
    }
    useEffect(() => {
        datasevendays();
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    /*useEffect(() => {
        const formatDate = (date) => {
            var dd = date.getDate();
            var mm = date.getMonth() + 1;
            var yyyy = date.getFullYear();
            if (dd < 10) { dd = '0' + dd }
            if (mm < 10) { mm = '0' + mm }
            date = yyyy + '-' + mm + '-' + dd;
            return date
        }
        var today = new Date();
        var dd = String(today.getDate()).padStart(2, '0');
        var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        var yyyy = today.getFullYear();
        today = mm + '/' + dd + '/' + yyyy;
        var result = [];
        for (var i = 0; i < 8; i++) {
            if(searchval.end_date && searchval.end_date!==''){
                var d = new Date(searchval.end_date);
            }else{
                var d = new Date(today);
            }
            d.setDate(d.getDate() - i);
            var dd = d.getDate();
            var mm = d.getMonth() + 1;
            var yyyy = d.getFullYear();
            if (dd < 10) { dd = '0' + dd }
            if (mm < 10) { mm = '0' + mm }
            var datefinal = yyyy + '-' + mm + '-' + dd;
            alert(datefinal)
            result.push(datefinal)
        }
        setDateseven(result);
    }, [])*/
    const getUserList = async () => {
        let res = await ghUserData(searchval);
        //console.log(res.data.tasklistdata)
        if (res.data.message === 'user list fetched') {
            setAllUserlist(res.data.userdatalist)
            setUserlist(res.data.userdata)
            setTasklist(res.data.tasklistdata)
        } else {
            alert(res.data.message)
        }
    }
    useEffect(() => {
        if (props.loggedinuser.user_id) {
            setSearchval({ ...searchval, gh_id: props.loggedinuser.user_id });
        }
    }, [props.loggedinuser.user_id]) // eslint-disable-line react-hooks/exhaustive-deps
    useEffect(() => {
        getUserList();
    }, []) // eslint-disable-line react-hooks/exhaustive-deps

    //console.log("SearchVal State", searchval)
    return (
        <>
            <div className="login" style={{ marginTop: '90px', width: '95%', overflow: 'overlay', maxWidth: '95%' }}>
                <div style={{ width: '100%' }}>
                    <div style={{ width: '45%', float: 'left' }}>
                        <div style={{ width: '20%', float: 'left', padding: '0.9rem 0.2rem' }}>
                            <b>Date Range: </b>
                        </div>
                        <div style={{ width: '40%', float: 'left' }}>
                            <DatePicker className="myinputclass" placeholder="From Date" name="start_date" selected={searchval.start_date} onChange={(date) => { setSearchval({ ...searchval, start_date: date }) }} />
                        </div>
                        <div style={{ width: '40%', float: 'left' }}>
                            <DatePicker className="myinputclass" placeholder="To Date" name="end_date" selected={searchval.end_date} onChange={(date) => { setSearchval({ ...searchval, end_date: date }) }} />
                        </div>
                    </div>
                    <div style={{ width: '38%', float: 'left' }}>
                        <div style={{ width: '18%', float: 'left', padding: '0.9rem 0rem', marginLeft: '25px' }}>
                            <b>Members: </b>
                        </div>
                        <div style={{ width: '75%', float: 'left' }}>
                            <select style={{ width: '95%' }} className="myinputclass" name="resource" value={searchval.resource} onChange={(e) => { setSearchval({ ...searchval, resource: e.target.value }) }}>
                                <option value="">Please Select Team Member</option>
                                {
                                    AllUserlist.map((userdropdata) =>
                                        <option key={userdropdata.user_id} value={userdropdata.user_id}>{userdropdata.name}</option>)
                                }
                            </select>
                        </div>
                    </div>
                    <div style={{ width: '15%', float: 'right' }}>
                        <Button onClick={() => { datasevendays(); getUserList(); }} type="submit" style={{ marginTop: '10px', background: 'rgba(237, 28, 36, 1)', borderRadius: '3px', border: '0 none', padding: '12px 42px', fontSize: '16px', fontWeight: '500', lineHeight: '16px', cursor: 'pointer', textTransform: 'capitalize', color: 'rgba(255, 255, 255, 1)' }}>Search</Button>
                    </div>
                </div>

            </div>
            <div className="outsider" style={{ marginTop: '-75px' }}>
                <div className="login scroll" style={{ maxWidth: '95%' }}>
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th style={{ width: '25%', textAlign: 'center' }}>Team Members</th>
                                <th style={{ width: '25%', textAlign: 'center' }}>12PM</th>
                                <th style={{ width: '25%', textAlign: 'center' }}>3PM</th>
                                <th style={{ width: '25%', textAlign: 'center' }}>6PM</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                dateseven.map((dataval, indexdate) =>
                                    <React.Fragment key={indexdate}>
                                        <tr>
                                            <th style={{ textAlign: 'center', backgroundColor: 'lightskyblue' }} colSpan="4">{dataval}</th>
                                        </tr>
                                        {
                                            userlist.map((userdata, index) =>
                                                <React.Fragment key={index}>
                                                    <tr>
                                                        <td>{userdata.name}&nbsp;({userdata.employee_code})</td>
                                                        {
                                                            tasklist.map((taskdata, indextask) =>
                                                                <Tasklist key={indextask} dateblock={dataval} userid={userdata.user_id} tasklist={taskdata} />
                                                            )
                                                        }
                                                    </tr>
                                                    <tr>
                                                        <td colSpan="4"></td>
                                                    </tr>
                                                </React.Fragment>
                                            )
                                        }
                                    </React.Fragment>
                                )
                            }
                        </tbody>
                    </Table>
                </div>
            </div>
        </>
    )
}
export default Dashboardgh;
