import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom';
import axios from 'axios';
const Register = () => {
    let myButton = '';
    let { userID } = useParams();
    const history = useHistory();
    const [user, setUser] = useState({
        f_name: "",
        l_name: "",
        email: "",
        phone: "",
        usertype: "",
        password: "",
        confPassword: ""
    })

    useEffect(() => {
        axios.get("http://localhost:9001/user/userdata/" + userID).then((res) => {
            setUser({
                f_name: res.data.userdata.first_name,
                l_name: res.data.userdata.last_name,
                email: res.data.userdata.email,
                phone: res.data.userdata.phone,
                usertype: res.data.userdata.user_type
            })
        })
    }, [])

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    const addUser = () => {
        //const {f_name, l_name, email, phone, usertype, password, confPassword} = user
        axios.post("http://localhost:9001/user/addUser", user).then((res) => {
            if (res.data.message === 'User already registerd. Please use another email!') {
                alert(res.data.message);
            } else {
                alert(res.data.message);
                history.push("/users");
            }
        })
    }
    const updateUser = () => {
        axios.put(`http://localhost:9001/user/editUser/${userID}`, user).then((res) => {
            console.log(res);
        })
    }
    if(user){
        myButton = <div className="button" onClick={() => { updateUser() }}>Update User</div>
    }else{
        myButton = <div className="button" onClick={() => { addUser() }}>Add User</div>
    }
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="centerform">
                <div className="register">
                    <h1>{userID? "Update User": "Add User"}</h1>
                    <input type="text" value={user.f_name} placeholder="First Name" name="f_name" onChange={inputVal} />
                    <input style={{ marginLeft: "15px" }} type="text" value={user.l_name} placeholder="Last Name" name="l_name" onChange={inputVal} />
                    <input type="text" value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />
                    <input style={{ marginLeft: "15px" }} type="text" value={user.phone} placeholder="Phone" name="phone" onChange={inputVal} />
                    <select name="usertype" value={user.usertype} onChange={inputVal}>
                        <option value="">
                            Please Select User Type
                        </option>
                        <option value="Super Admin">
                            Super Admin
                        </option>
                        <option value="Admin">
                            Admin
                        </option>
                        <option value="Staff">
                            Staff
                        </option>
                        <option value="Account">
                            Account
                        </option>
                    </select>

                    <input type="password" value={user.password} placeholder="Password" name="password" onChange={inputVal} />
                    <input style={{ marginLeft: "15px" }} type="password" value={user.confPassword} placeholder="Confirm Password" name="confPassword" onChange={inputVal} />
                    {myButton}
                    {/*<div>OR</div>
                    <div className="button" onClick={() => { history.push("/login") }}>Login </div>*/}
                    {/* OR <Link to="/login">Login</Link> */}
                </div>
            </div>
        </>
    )
}
export default Register;
