import React, { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import {Form, Row, Group, Col, Label, Control, Button, Check, Select} from 'react-bootstrap'
const Register = () => {
    const history = useHistory();
    const [user, setUser] = useState({
        f_name: "",
        l_name: "",
        email: "",
        phone: "",
        usertype: "",
        password: "",
        confPassword: ""
    })
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (storedData._id && storedData._id !== "") {
            history.push("/");
        } else {
            history.push("/register");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    const registerUser = () => {
        //const {f_name, l_name, email, phone, usertype, password, confPassword} = user
        axios.post("http://localhost:9001/user/register", user).then((res) => {
            if (res.data.message === 'User already registerd. Please use another email!') {
                alert(res.data.message);
            } else {
                alert(res.data.message);
                history.push("/login");
            }
        })
    }
    return (
        <>

<Form>
  <Row className="mb-3">
    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Email</Form.Label>
      <Form.Control type="email" placeholder="Enter email" />
    </Form.Group>

    <Form.Group as={Col} controlId="formGridPassword">
      <Form.Label>Password</Form.Label>
      <Form.Control type="password" placeholder="Password" />
    </Form.Group>
  </Row>

  <Form.Group className="mb-3" controlId="formGridAddress1">
    <Form.Label>Address</Form.Label>
    <Form.Control placeholder="1234 Main St" />
  </Form.Group>

  <Form.Group className="mb-3" controlId="formGridAddress2">
    <Form.Label>Address 2</Form.Label>
    <Form.Control placeholder="Apartment, studio, or floor" />
  </Form.Group>

  <Row className="mb-3">
    <Form.Group as={Col} controlId="formGridCity">
      <Form.Label>City</Form.Label>
      <Form.Control />
    </Form.Group>

    <Form.Group as={Col} controlId="formGridState">
      <Form.Label>State</Form.Label>
      <Form.Select>
        
      </Form.Select>
    </Form.Group>

    <Form.Group as={Col} controlId="formGridZip">
      <Form.Label>Zip</Form.Label>
      <Form.Control />
    </Form.Group>
  </Row>

  <Form.Group className="mb-3" id="formGridCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>

            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="register">
                <h1>Register</h1>
                <input type="text" value={user.f_name} placeholder="First Name" name="f_name" onChange={inputVal} />
                <input type="text" value={user.l_name} placeholder="Last Name" name="l_name" onChange={inputVal} />
                <input type="text" value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />
                <input type="text" value={user.phone} placeholder="Phone" name="phone" onChange={inputVal} />
                <select name="usertype" value={user.usertype} onChange={inputVal}>
                    <option value="">
                        Please Select User Type
                    </option>
                    <option value="Super Admin">
                        Super Admin
                    </option>
                    <option value="Admin">
                        Admin
                    </option>
                    <option value="Staff">
                        Staff
                    </option>
                    <option value="Account">
                        Account
                    </option>
                </select>

                <input type="password" value={user.password} placeholder="Password" name="password" onChange={inputVal} />
                <input type="password" value={user.confPassword} placeholder="Confirm Password" name="confPassword" onChange={inputVal} />
                <div className="button" onClick={() => { registerUser() }}>Register</div>
                <div>OR</div>
                <div className="button" onClick={() => { history.push("/login") }}>Login {/* OR <Link to="/login">Login</Link> */}</div>
            </div>
        </>
    )
}
export default Register;
