import axios from 'axios';
const usersUrl = 'http://localhost:9001/category';

export const deleteCategory = async (id) => {
    return await axios.delete(`${usersUrl}/userdelete/${id}`);
}

export const changecategoryStatus = async (id) => {
    return await axios.put(`${usersUrl}/changeStatus/${id}`);
}

export const getCategories = async (sortingparams, searchparams) => {
    return await axios.get(`${usersUrl}/userlist${sortingparams}${searchparams}`);
}

export const getsingleCategory = async (userID) => {
    return await axios.get(`${usersUrl}/userdata/${userID}`);
}

export const addCategory = async (user) => {
    return await axios.post(`${usersUrl}/addUser`, user);
}

export const editCategory = async (userID, user) => {
    return await axios.put(`${usersUrl}/editUser/${userID}`, user)
}
