import { useEffect, useState } from "react"
import { getImages } from '../Services/productapi'
const Productimages = (props) => {
    const [productpic, setProductpic] = useState([]);
    useEffect(()=>{
        async function imagesDisplay(){
            if(props.prodID && props.prodID!=='' && props.prodID!==undefined){
                let response = getImages(props.prodID);
                console.log(response);

            }
        }
    }, [props.prodID])
    alert(props.prodID);
    return (
        <div style={{overflow:'hidden', height:'1%'}}>
            {
                props.prodID ?
                    props.product.imageuploaded && props.product.imageuploaded === 'true' ?
                        "" :
                        props.product.productPictureviewonly && props.product.productPictureviewonly.length > 0 ?
                            props.product.productPictureviewonly.map(option =>
                                <div className="image-area">
                                    <img key={option._id} alt="" src={process.env.React_App_PUBLIC_URL_PRODUCTS + `${option.img}`} style={{ width: '125px', margin: '5px' }} />
                                    <a className="remove-image" style={{display:'inline', cursor:'pointer'}}>&#215;</a>
                                </div>
                            )
                            : ""
                    : ""
            }
        </div>
    )
}
export default Productimages
