import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';
import axios from 'axios';
const Register = () => {
const history = useHistory();
const [user, setUser] = useState({
    f_name: "",
    l_name: "",
    email: "",
    phone: "",
    usertype: "",
    password: "",
    confPassword : ""
})
const inputVal = (e) => {
    const {name, value} = e.target;
    setUser({
        ...user,
        [name]: value
    })
}
const registerUser = () => {
    //const {f_name, l_name, email, phone, usertype, password, confPassword} = user
    axios.post("http://localhost:9001/user/register", user).then((res) => {
        alert(res.data.message);
        history.push("/login");
    })
}
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="register">
                 <h1>Register</h1>
                <input type="text" value={user.f_name} placeholder="First Name" name="f_name" onChange={inputVal} />
                <input type="text" value={user.l_name} placeholder="Last Name" name="l_name" onChange={inputVal} />
                <input type="text" value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />
                <input type="text" value={user.phone} placeholder="Phone" name="phone" onChange={inputVal} />
                <select name="usertype" value={user.usertype} onChange={inputVal}>
                    <option value="">
                        Please Select User Type
                    </option>
                    <option value="Super Admin">
                        Super Admin
                    </option>
                    <option value="Admin">
                        Admin
                    </option>
                    <option value="Staff">
                        Staff
                    </option>
                    <option value="Account">
                        Account
                    </option>
                </select>

                <input type="password" value={user.password} placeholder="Password" name="password" onChange={inputVal} />
                <input type="password" value={user.confPassword} placeholder="Confirm Password" name="confPassword" onChange={inputVal} />
                <div className="button" onClick={registerUser}>Register</div>
                <div>OR</div>
                <div className="button" onClick={() => {history.push("/login")}}>Login {/* OR <Link to="/login">Login</Link> */}</div>
            </div>
        </>
    )
}
export default Register;
