import './App.css';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Page_not_found_404 from './components/Page_not_found_404';
import Protected from './components/Protected';
import { Switch, Route } from 'react-router-dom';
import { useEffect, useState } from 'react';
function App() {
  const [logUser, setLogUser] = useState({});
  //const history = useHistory();
  useEffect(() => {
    setLogUser(JSON.parse(localStorage.getItem("MyUser")));
  }, [])
  const updateUserinlocalStorage = (user) => {
    localStorage.setItem("MyUser", JSON.stringify(user));
    setLogUser(user);
  }

  return (
    <div className="App">
      <Switch>
      <Route path="/" exact={true}>
          {
            logUser && logUser._id ? <Protected component={Dashboard} logUser={logUser} updateUserinlocalStorage={updateUserinlocalStorage} /> : <Protected component={Login} logUser={logUser} updateUserinlocalStorage={updateUserinlocalStorage} />
          }
        </Route>
        <Route path="/register"><Protected component={Register} logUser={logUser} /></Route>
        <Route path="/login"><Protected component={Login} logUser={logUser} updateUserinlocalStorage={updateUserinlocalStorage} /></Route>
        <Route path="*"><Page_not_found_404 /></Route>
      </Switch>
    </div>
  );
}
export default App;
