import axios from 'axios';
const usersUrl = 'http://localhost:9001/product';

export const deleteProduct = async (id) => {
    return await axios.delete(`${usersUrl}/removeProd/${id}`);
}

export const changeproductStatus = async (id) => {
    return await axios.put(`${usersUrl}/changeStatus/${id}`);
}

export const getProducts = async (sortingparams, searchparams) => {
    return await axios.get(`${usersUrl}/getAllProduct${sortingparams}${searchparams}`);
}

export const getsingleProduct = async (userID) => {
    return await axios.get(`${usersUrl}/singleProduct/${userID}`);
}

export const addProduct = async (user) => {
    return await axios.post(`${usersUrl}/addCat`, user);
}

export const editsingleProduct = async (userID, user) => {
    return await axios.put(`${usersUrl}/editCategory/${userID}`, user)
}

export const getSubcategoryName = async (parentid) => {
    return await axios.get(`${usersUrl}/getSubcat/${parentid}`)
}

export const getCategoryList = async () => {
    return await axios.get(`${usersUrl}/getSubcatList`)
}
