import { Table, Container, DropdownButton, Dropdown, Button, Pagination, Spinner } from 'react-bootstrap'
import React, { useEffect, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom'
import Solicitation from './Solicitation';
import { getSoldata } from '../Services/solicitationapi'
import "./paginationstyle.css";
const Solsearchresult = (props) => {
    const history = useHistory();
    const location = useLocation();
    const [loader, setLoader] = useState(false);
    const [fbofinaldata, setFbofinaldata] = useState([]);

    // Pagination Code Starts Here

    const pageNumberLimit = 5;

    const [currentPage, setcurrentPage] = useState(1);
    const [itemsPerPage, setitemsPerPage] = useState(5);
    //const [pageNumberLimit, setpageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);
    const handleClick = (event) => {
        setcurrentPage(Number(event.target.id));
    };
    const pages = [];
    for (let i = 1; i <= Math.ceil(fbofinaldata.length / itemsPerPage); i++) {
        pages.push(i);
    }
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = fbofinaldata.slice(indexOfFirstItem, indexOfLastItem);
    const renderPageNumbers = pages.map((number) => {
        if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
            return (
                <li
                    key={number}
                    id={number}
                    onClick={handleClick}
                    className={currentPage === number ? "active" : null}
                >
                    {number}
                </li>
            );
        } else {
            return null;
        }
    });
    const handleNextbtn = () => {
        setcurrentPage(currentPage + 1);
        if (currentPage + 1 > maxPageNumberLimit) {
            setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
        }
    };
    const handlePrevbtn = () => {
        setcurrentPage(currentPage - 1);
        if ((currentPage - 1) % pageNumberLimit === 0) {
            setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
        }
    };
    let pageIncrementBtn = null;
    if (pages.length > maxPageNumberLimit) {
        pageIncrementBtn = <li onClick={handleNextbtn}> &hellip; </li>;
    }
    let pageDecrementBtn = null;
    if (minPageNumberLimit >= 1) {
        pageDecrementBtn = <li onClick={handlePrevbtn}> &hellip; </li>;
    }
    /*
    const handleLoadMore = () => {
        setitemsPerPage(itemsPerPage + 5);
    };
    */
    // Pagination Code Ends Here

    // Check if localstorage is set or not. If not set than redirect to the home page
    useEffect(() => {
        document.title = 'Solicitation Search Result';
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const Solsearchresult = async () => {
        setLoader(true)
        const response = await getSoldata(location.data)
        if (response.data.message && response.data.message === 'Data fetched Successfully') {
            setFbofinaldata(response.data.fbodata);
        } else {
            alert(response.data.message)
        }
        setLoader(false)
    }
    useEffect(() => {
        Solsearchresult();
    }, [location.data])
    return (
        <>
            <Container className="maindivmargin" style={{ textAlign: 'center' }}>
                {
                    loader ? <img src="/images/ajax-spinner.gif" alt="Loader" /> : <Solicitation searchoption={props.searchoption} searchimage={props.searchimage} />
                }
                {
                    !loader ?

                        <div>

                            <Table responsive striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Posted Date <button onClick={() => { sortingdata("date", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("date", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button> </th>
                                        <th>Subject <button onClick={() => { sortingdata("subject", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("subject", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button></th>
                                        <th>Solnbr# <button onClick={() => { sortingdata("solnbr", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("solnbr", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button></th>
                                        <th>Agency/Office <button onClick={() => { sortingdata("agency", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("agency", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button></th>
                                        <th>Naics/PSC <button onClick={() => { sortingdata("naics", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("user_type", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        !loading ?
                                            currentItems.map((datafbo, i) =>
                                                <tr key={i}>
                                                    <td>{i + 1}</td>
                                                    <td>{datafbo.date}</td>
                                                    <td>{datafbo.subject}</td>
                                                    <td>{datafbo.solnbr}</td>
                                                    <td>{datafbo.agency}<br />{datafbo.office}</td>
                                                    <td>{datafbo.naics}<br />{datafbo.classcod}</td>
                                                </tr>
                                            ) : <tr><td colSpan="6" style={{ paddingTop: '20px', paddingBottom: '20px' }}><Spinner animation="border" role="status">
                                                <span className="visually-hidden">Loading...</span>
                                            </Spinner></td></tr>
                                    }
                                </tbody>
                            </Table>
                        </div>
                        : ""
                }

            </Container>
        </>
    )
}
export default Solsearchresult;
