import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
import Pagination from 'reactjs-hooks-pagination';
const pageLimit = 5;
const User = () => {
    const history = useHistory();
    const [userdata, setUserdata] = useState([]);

    const [loading, setLoading] = useState(true)
    const [error, setError] = useState('')
    const [totalRecords, setTotalRecords] = useState(50);
    const [currentPage, setCurrentPage] = useState(1);

    useEffect(() => {
        userList()
    }, [])
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        //console.log(storedData)
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const removeUser = (user_id, name) => {
        var result = window.confirm("Want to delete the User " + name + "?");
        if (result) {
            axios.delete("http://localhost:9001/user/userdelete/" + user_id).then((res) => {
                if (res.data.message && res.data.message === 'user deleted successfully') {
                    alert(res.data.message);
                    userList()
                }
            })
        }
    }
    const statusUser = (user_id) => {
        axios.put("http://localhost:9001/user/changeStatus/" + user_id).then((res) => {
            if (res.data.message && res.data.message === 'user status updated successfully') {
                alert(res.data.message);
                userList()
            }
        })
    }
    const userList = () => {
       /* axios.get("http://localhost:9001/user/userList").then((res) => {
            setUserdata(res.data.userdata);
        })
        */

        axios.get('http://localhost:9001/user/userList?page=' + currentPage + '&limit=' + pageLimit)
            .then(response => {
                setLoading(false)
                setUserdata(response.data.userdata)
                setTotalRecords(response.data.userdata.length)
                setError('')
            })
            .catch(error => {
                setLoading(false)
                setUserdata([])
                setTotalRecords(50)
                setError('Something went wrong')
            })

    }
    return (
        <Container className="maindivmargin">

            <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>

            <div className="userlist">
                <h2>Users List</h2>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>User Type</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {loading ? (<div>Loading ...</div>) : (
                            userdata.map((userdata, i) =>
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{userdata.first_name}</td>
                                    <td>{userdata.last_name}</td>
                                    <td>{userdata.email}</td>
                                    <td>{userdata.phone}</td>
                                    <td>{userdata.user_type}</td>
                                    <td>{userdata.status ? "Active" : "Inactive"}</td>
                                    <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                        {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                                        <Dropdown.Item onClick={() => { history.push("/updateUser/" + userdata._id) }}>Edit User</Dropdown.Item>
                                        {
                                            JSON.parse(localStorage.getItem("MyUser"))._id && JSON.parse(localStorage.getItem("MyUser"))._id !== userdata._id ?
                                                <> <Dropdown.Item onClick={() => { removeUser(userdata._id, userdata.first_name) }}>Delete User</Dropdown.Item>
                                                    <Dropdown.Item onClick={() => { statusUser(userdata._id) }}>Change Status</Dropdown.Item></>
                                                :
                                                ""
                                        }
                                    </DropdownButton></td>
                                </tr>
                            )
                        )}
                    </tbody>
                </Table>
                <div className="d-flex flex-row py-4 justify-content-end">
                    <Pagination
                        totalRecords={totalRecords}
                        pageLimit={pageLimit}
                        pageRangeDisplayed={1}
                        onChangePage={setCurrentPage}
                    />
                </div>
            </div>
        </Container>
    )
}
export default User
