// Search Options:--
// Keyword search
// posted date search (date search)
// response date search (date search)
// place of performance state search option (multiple checkboxes)
// place of performance zipcode search (comma saparated zipcode)
// Naics code search (multiple checkbox - this is also separted search)
// PSC code search (multiple checkbox - this is also separted search)
// Federal set-aside code search (multiple checkbox - this is also separted search)
// Agency and Subagency Search (multiple search  - this is also separted search)
import React, { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap'
import DatePicker from 'react-datepicker';
import { useHistory } from 'react-router-dom';
import "react-datepicker/dist/react-datepicker.css"
import { getNaics, getPsc, getAllsetaside, getAllstates, getAgency } from '../Services/solicitationapi'
const SolicitationSearchBar = (props) => {
    const history = useHistory();
    const [searchform, setSearchform] = useState({
        keyword: '',
        postedfromDate: '',
        postedtoDate: '',
        statepref: [],
        zipcode: '',
        responsefromDate: '',
        responsetoDate: '',
        naics: [],
        psc: [],
        aside: [],
        naicstext: "",
        psctext: "",
        agencytext: "",
        naresponse: false,
        agency: [],
        office: []
    })
    const [agencygetdata, setAgencygetdata] = useState([]);
    const [naicsgetdata, setNaicsgetdata] = useState([]);
    const [pscgetdata, setPscgetdata] = useState([]);
    const [setasidegetdata, setSetasidegetdata] = useState([]);
    const [states, setStates] = useState([]);
    const inputVal = (e) => {
        const { name, value } = e.target;
        setSearchform({
            ...searchform,
            [name]: value
        })
    }
    const checkVal = (e) => {
        const statepref = searchform.statepref;
        let index;
        if (e.target.checked) {
            statepref.push(e.target.value)
        } else {
            index = statepref.indexOf(e.target.value)
            statepref.splice(index, 1)
        }
        setSearchform({
            ...searchform,
            statepref: statepref
        })
    }
    const checkValnaics = (e) => {
        const naics = searchform.naics
        let index;
        if (e.target.checked) {
            naics.push(e.target.value)
        } else {
            index = naics.indexOf(e.target.value)
            naics.splice(index, 1)
        }
        setSearchform({
            ...searchform,
            naics: naics
        })
    }
    const checkValpsc = (e) => {
        const psc = searchform.psc
        let index;
        if (e.target.checked) {
            psc.push(e.target.value)
        } else {
            index = psc.indexOf(e.target.value)
            psc.splice(index, 1)
        }
        setSearchform({
            ...searchform,
            psc: psc
        })
    }
    const checkValaside = (e) => {
        const aside = searchform.aside
        let index;
        if (e.target.checked) {
            aside.push(e.target.value)
        } else {
            index = aside.indexOf(e.target.value)
            aside.splice(index, 1)
        }
        setSearchform({
            ...searchform,
            aside: aside
        })
    }
    const checkValagency = (e) => {
        const agency = searchform.agency
        let index;
        if (e.target.checked) {
            agency.push(e.target.value)
        } else {
            index = agency.indexOf(e.target.value)
            agency.splice(index, 1)
        }
        setSearchform({
            ...searchform,
            agency: agency
        })
    }
    const checkValoffice = (e) => {
        const office = searchform.office
        let index;
        if (e.target.checked) {
            office.push(e.target.value)
        } else {
            index = office.indexOf(e.target.value)
            office.splice(index, 1)
        }
        setSearchform({
            ...searchform,
            office: office
        })
    }
    const checkValnaresponse = (e) => {
        if (e.target.checked) {
            setSearchform({
                ...searchform,
                naresponse: true
            })
        } else {
            setSearchform({
                ...searchform,
                naresponse: false
            })
        }
    }
    const naicsupdate = async (e) => {
        const { name, value } = e.target;
        setSearchform({
            ...searchform,
            [name]: value
        })
        const response = await getNaics(e.target.value);
        setNaicsgetdata(response.data.naicsdata)
    }
    const pscupdate = async (e) => {
        const { name, value } = e.target;
        setSearchform({
            ...searchform,
            [name]: value
        })
        const response = await getPsc(e.target.value);
        setPscgetdata(response.data.pscdata)
    }
    const agencyupdate = async (e) => {
        const { name, value } = e.target;
        setSearchform({
            ...searchform,
            [name]: value
        })
        const response = await getAgency(e.target.value);
        setAgencygetdata(response.data.agencydata)
    }
    useEffect(() => {
        const getAllsetasidedata = async () => {
            const response = await getAllsetaside();
            setSetasidegetdata(response.data.setasidedata)

            const res = await getAllstates();
            setStates(res.data.setstatedata)
        }
        getAllsetasidedata()
    }, [])

    const searchSol = () => {
        history.push({ pathname: "/solresult", data: searchform });
    }
    /*
    setTimeout(() => {
        alert("Hi Ankit");
    }, 2000);*/
    /*useEffect(() => {
        const delayDebounceFn = setTimeout(() => {
          console.log(searchform.naicstext)
          const response = await getNaics(e.target.value);
            setNaicsgetdata(response.data.naicsdata)
        }, 3000)
    
        return () => clearTimeout(delayDebounceFn)
      }, [searchform.naicstext]) */
    //console.log(searchform);
    console.log("Form Data", searchform);
    return (
        <div className="solicitation">
            <div>
                <div style={{ width: '100%' }}>
                    <strong>I am looking for...</strong>
                    <input className="myinputclass" type="text" value={searchform.keyword} placeholder="Keyword" name="keyword" onChange={inputVal} />
                </div>

                <div style={{ width: '100%', marginTop: '15px' }}>
                    <strong>Posted Date (Single or Range)</strong><br />
                    <div style={{ float: 'left', width: '47%', marginLeft: '2.6%' }}>
                        <DatePicker className="myinputclass" placeholder="From Date" name="postedfromDate" selected={searchform.postedfromDate} onChange={(date) => { setSearchform({ ...searchform, postedfromDate: date }) }} />
                    </div>
                    <div style={{ float: 'right', width: '47%', marginRight: '2.6%' }}>
                        <DatePicker className="myinputclass" placeholder="To Date" name="postedtoDate" selected={searchform.postedtoDate} onChange={(date) => { setSearchform({ ...searchform, postedtoDate: date }) }} />
                    </div>
                </div>

                <div style={{ width: '100%', marginTop: '70px' }}>
                    <strong>Response Date (Single or Range)</strong><br />
                    <div style={{ float: 'left', width: '47%', marginLeft: '2.6%' }}>
                        <DatePicker className="myinputclass" placeholder="From Date" name="responsefromDate" selected={searchform.responsefromDate} onChange={(date) => { setSearchform({ ...searchform, responsefromDate: date }) }} />
                    </div>
                    <div style={{ float: 'right', width: '47%', marginRight: '2.6%' }}>
                        <DatePicker className="myinputclass" placeholder="To Date" name="responsetoDate" selected={searchform.responsetoDate} onChange={(date) => { setSearchform({ ...searchform, responsetoDate: date }) }} />
                    </div>
                </div>

                <div style={{ width: '100%', minHeight: '76px', marginTop: '70px' }}>
                    <div style={{ float: 'left', width: '50%' }}>
                        <strong>Place of Performance State: </strong>
                        <div className="chkbox-area" style={{ marginLeft: '11%' }}>
                            {
                                states.map((datastate, indexkey) =>
                                    <div key={indexkey}>
                                        <div style={{ float: 'left' }}>
                                            <input type="checkbox" onChange={checkVal} name="statepref[]" value={datastate.Code} />
                                        </div>
                                        <div style={{ float: 'left' }}>
                                            &nbsp; {datastate.State}
                                        </div><br />
                                    </div>
                                )
                            }
                        </div>
                    </div>

                    <div style={{ float: 'right', width: '50%' }}>
                        <strong>Place of Performance Zip Code: </strong>
                        <textarea onChange={inputVal} name="zipcode" className="prfm_state" value={searchform.zipcode} style={{ marginRight: '5%' }} />
                    </div>

                </div>

                <div style={{ width: '100%', marginTop: '88px', color: 'red' }}>
                    <input type="checkbox" onChange={checkValnaresponse} name="noresponsedate[]" value={searchform.naresponse} />&nbsp; I do not want N/A response dates as part of my search results
                </div>

                <div style={{ width: '100%', marginTop: '35px' }}>
                    <hr />
                    <div style={{ float: 'left' }}>
                        <h5>Advanced Search</h5>
                    </div>
                </div>

                <div style={{ width: '100%', marginTop: '75px' }}>
                    <strong>NAICS Codes:</strong><br />
                    <input className="myinputclass" type="text" value={searchform.naicstext} placeholder="Enter Keyword or 2-6 digit code" name="naicstext" onChange={naicsupdate} />
                    {
                        naicsgetdata.length > 0 ?
                            <div className="chkbox-area" style={{ height: '200px', width: '100%' }}>
                                {
                                    naicsgetdata.map((datanaics, index) =>
                                        <div key={index}>
                                            <div style={{ float: 'left' }}>
                                                <input type="checkbox" onChange={checkValnaics} name="naics[]" value={datanaics.ten_id_show} />
                                            </div>
                                            <div style={{ float: 'left' }}>
                                                &nbsp; {datanaics.ten_id_show} - {datanaics.ten_cat_name}
                                            </div><br />
                                        </div>
                                    )
                                }
                            </div> : ""
                    }
                </div>

                <div style={{ width: '100%', marginTop: '35px' }}>
                    <strong>PSC Code:</strong><br />
                    <input className="myinputclass" type="text" placeholder="Enter PSC Code/description" name="psctext" value={searchform.psctext} onChange={pscupdate} />
                    {
                        pscgetdata.length > 0 ?
                            <div className="chkbox-area" style={{ height: '200px', width: '100%' }}>
                                {
                                    pscgetdata.map((datapsc, ind) =>
                                        <div key={ind}>
                                            <div style={{ float: 'left' }}>
                                                <input type="checkbox" onChange={checkValpsc} name="psc[]" value={datapsc.Class_id} />
                                            </div>
                                            <div style={{ float: 'left' }}>
                                                &nbsp; {datapsc.Class_id} - {datapsc.Classification}
                                            </div><br />
                                        </div>
                                    )
                                }
                            </div> : ""
                    }
                </div>

                <div style={{ width: '100%', marginTop: '35px' }}>
                    <strong>Federal Set-Aside Code:</strong> <br />
                    <div className="chkbox-area" style={{ height: '250px', width: '100%' }}>
                        {
                            setasidegetdata.map((asidedata, i) =>
                                <div key={i}>
                                    <div style={{ float: 'left' }}>
                                        <input type="checkbox" onChange={checkValaside} name="aside[]" value={asidedata.aside_names} />
                                    </div>
                                    <div style={{ float: 'left' }}>
                                        &nbsp; {asidedata.aside_name}
                                    </div><br />
                                </div>
                            )
                        }
                    </div>
                </div>

                <div style={{ width: '100%', marginTop: '35px' }}>
                    <strong>Agency/Entity: </strong><br />
                    <input className="myinputclass" type="text" value={searchform.agencytext} placeholder="Enter Keyword" name="agencytext" onChange={agencyupdate} />
                    {
                        agencygetdata.length > 0 ?
                            <div className="chkbox-area" style={{ height: '350px', width: '100%' }}>
                                {
                                    agencygetdata.map((dataagency, indexag) =>
                                        <div key={indexag} style={{ width: '100%', border: 'solid 1px lightgray', float:'left', marginBottom:'1%' }}>
                                            <div style={{ width: '45%', float: 'left', margin:'5px' }}>
                                                <div style={{ float: 'left', width:'5%' }}>
                                                    <input type="checkbox" onChange={checkValagency} name="agency[]" value={dataagency.agencyID+'-'+dataagency.agencyName} />
                                                </div>
                                                <div style={{ float: 'left', width:'95%', textAlign:'left' }}>
                                                    &nbsp; {dataagency.agencyName}
                                                </div><br />
                                            </div>
                                            <div style={{ width: '53%', float: 'right' }}>
                                                {
                                                    dataagency.office.map((dataoffice, indexoff) =>
                                                        <div key={indexoff}>
                                                        <div style={{ float: 'left', width:'5%' }}>
                                                            <input type="checkbox" onChange={checkValoffice} name="office[]" value={dataagency.agencyID+'-'+dataoffice.name} />
                                                        </div>
                                                        <div style={{ float: 'left', width:'95%', textAlign:'left' }}>
                                                            &nbsp; {dataoffice.name}
                                                        </div><br />
                                                        </div>
                                                    )
                                                }
                                            </div>
                                        </div>
                                    )}
                            </div> : ""
                    }
                </div>

                <div style={{ width: '100%', marginTop: '30px' }}>
                    <Button onClick={searchSol}>Search</Button>
                </div>

            </div>
        </div >
    )
}

export default SolicitationSearchBar;
