// Search Options:--
// Keyword search
// posted date search (date search)
// response date search (date search)
// place of performance state search option (multiple checkboxes)
// place of performance zipcode search (comma saparated zipcode)
// Naics code search (multiple checkbox - this is also separted search)
// PSC code search (multiple checkbox - this is also separted search)
// Federal set-aside code search (multiple checkbox - this is also separted search)
// Agency and Subagency Search (multiple search  - this is also separted search)
import React, { useState } from 'react';
import DatePicker from 'react-datepicker'
import "react-datepicker/dist/react-datepicker.css"
const SolicitationSearchBar = () => {
    const [startDate, setStartDate] = useState(new Date());
    const [searchform, setSearchform] = useState({
        keyword: ""
    })
    const inputVal = (e) => {
        const { name, value } = e.target;
        setSearchform({
            ...searchform,
            [name]: value
        })
    }
    return (
        <div className="solicitation">
            <div>
                <div style={{ width: '100%', minHeight: '76px' }}>
                    <div style={{ float: 'left', width: '33%' }}>
                        I am looking for...
                        <input className="myinputclass" type="text" value="" placeholder="Keyword" name="keyword" onChange={inputVal} />
                    </div>
                    <div style={{ float: 'left', width: '63%' }}>
                        Posted Date (Single or Range)<br />
                        <input className="myinputclass" style={{ marginLeft: "15px", width: '40%' }} type="text" value="" placeholder="From Date" name="l_name" onChange={inputVal} />
                        &nbsp;&nbsp;to
                        <input className="myinputclass" style={{ marginLeft: "15px", width: '40%' }} type="text" value="" placeholder="To Date" name="l_name" onChange={inputVal} />
                    </div>
                </div>
                <div style={{ width: '100%', minHeight: '76px' }}>
                    <div style={{ float: 'left', width: '50%' }}>
                        <input className="myinputclass" type="text" value="" placeholder="Email Address" name="email" onChange={inputVal} />
                    </div>
                    <div style={{ float: 'right', width: '50%' }}>
                        <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" value="" placeholder="Phone" name="phone" onChange={inputVal} />
                    </div>
                </div>
                <div style={{ width: '100%', minHeight: '76px' }}>
                    <select style={{ width: '95%' }} className="myinputclass" name="usertype" value="" onChange={inputVal}>
                        <option value="">
                            Please Select User Type
                        </option>
                        <option value="Super Admin">
                            Super Admin
                        </option>
                        <option value="Admin">
                            Admin
                        </option>
                        <option value="Staff">
                            Staff
                        </option>
                        <option value="Account">
                            Account
                        </option>
                    </select>
                </div>

                <div style={{ width: '100%', minHeight: '76px' }}>
                    <div style={{ float: 'left', width: '50%' }}>
                        <input className="myinputclass" type="text" value="" placeholder="Password" autoComplete="on" name="password" onChange={inputVal} />
                    </div>
                    <div style={{ float: 'right', width: '50%' }}>
                        <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" autoComplete="on" value="" placeholder="Confirm Password" name="confPassword" onChange={inputVal} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default SolicitationSearchBar;
