import axios from 'axios'
const solUrl = 'http://localhost:9001/solicitation';

export const getNaics = async (searchterm) => {
    return await axios.get(`${solUrl}/getNaicsdata/${searchterm}`);
}

export const getPsc = async (searchterm) => {
    return await axios.get(`${solUrl}/getPscdata/${searchterm}`);
}

export const getAllsetaside = async () => {
    return await axios.get(`${solUrl}/setasideData`)
}

export const getAllstates = async () => {
    return await axios.get(`${solUrl}/stateData`)
}

export const getSoldata = async (searchdata) => {
    return await axios.get(`${solUrl}/solData`, searchdata)
}
