import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button, Pagination, Spinner } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
//import axios from 'axios';
import { deleteProduct, changeproductStatus, getProducts } from '../Services/productapi'
import "./paginationstyle.css";
const Products = () => {
    const history = useHistory();
    // Main Product list data return State
    const [productdata, setProductdata] = useState([]);
    // Total Count State
    const [resultcount, setResultcount] = useState(0);
    // Loading bar State
    const [loading, setLoading] = useState(false);
    // Sorting State
    const [sortingcolumn, setSortingcolumn] = useState("name");
    const [sortingval, setSortingval] = useState(1);
    // This stated is used for search query parameter
    const [q, setQ] = useState("");

    // Pagination Code Starts Here
    const [currentPage, setcurrentPage] = useState(1);
    const [itemsPerPage, setitemsPerPage] = useState(5);
    const [pageNumberLimit, setpageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);
    const handleClick = (event) => {
        setcurrentPage(Number(event.target.id));
    };
    const pages = [];
    for (let i = 1; i <= Math.ceil(productdata.length / itemsPerPage); i++) {
        pages.push(i);
    }
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = productdata.slice(indexOfFirstItem, indexOfLastItem);
    const renderPageNumbers = pages.map((number) => {
        if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
            return (
                <li
                    key={number}
                    id={number}
                    onClick={handleClick}
                    className={currentPage === number ? "active" : null}
                >
                    {number}
                </li>
            );
        } else {
            return null;
        }
    });
    const handleNextbtn = () => {
        setcurrentPage(currentPage + 1);
        if (currentPage + 1 > maxPageNumberLimit) {
            setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
        }
    };
    const handlePrevbtn = () => {
        setcurrentPage(currentPage - 1);
        if ((currentPage - 1) % pageNumberLimit === 0) {
            setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
        }
    };
    let pageIncrementBtn = null;
    if (pages.length > maxPageNumberLimit) {
        pageIncrementBtn = <li onClick={handleNextbtn}> &hellip; </li>;
    }
    let pageDecrementBtn = null;
    if (minPageNumberLimit >= 1) {
        pageDecrementBtn = <li onClick={handlePrevbtn}> &hellip; </li>;
    }
    /*
    const handleLoadMore = () => {
        setitemsPerPage(itemsPerPage + 5);
    };
    */
    // Pagination Code Ends Here

    // This useEffect called first time when page load and when sortingval state and sortingcolumn state changed
    useEffect(() => {
        ProductsList()
    }, [sortingval, sortingcolumn]) // eslint-disable-line react-hooks/exhaustive-deps

    // Check if localstorage is set or not. If not set than redirect to the home page
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps


    // This funciton is called when remove the product from the list
    const removeProduct = async (id, name) => {
        var result = window.confirm("Want to delete the Product " + name + "?");
        if (result) {
            let response = await deleteProduct(id);
            if (response.data.message && response.data.message === 'Product deleted successfully!') {
                alert(response.data.message);
                ProductsList()
            }
        }
    }
    // This function is called when change the product status
    const statusProduct = async (id) => {
        let response = await changeproductStatus(id);
        if (response.data.message && response.data.message === 'Product status updated successfully') {
            alert(response.data.message);
            ProductsList()
        }
    }
    // This function is used for change the status of sorting
    const sortingdata = (colname, val) => {
        setSortingcolumn(colname)
        setSortingval(val)
    }

    // This function is used for fetch the all product data and in this function we have passed the sorting column and search parameter
    let sortingparams = '';
    let searchparams = '';
    const ProductsList = async () => {
        setLoading(true)
        sortingparams = '/' + sortingcolumn + '/' + sortingval;
        if (q) {
            setcurrentPage(1);
            setitemsPerPage(5);
            searchparams = '/' + q
        } else {
            searchparams = '/';
        }
        let response = await getProducts(sortingparams, searchparams);
        if (response.data.message && response.data.message === 'product list fetched') {
            setResultcount(response.data.productdata.length);
            setProductdata(response.data.productdata);
            //console.log(response.data.catdata)        
        }
        setLoading(false)
    }
    /*const clearSearch=()=>{
        setQ("")
        alert(q)
        alert("AAAAAA");
        ProductsList()
    }
    */
    return (
        <Container className="maindivmargin">
            <div style={{ width: '100%' }}>
                <div style={{ width: '60%', float: 'left' }}>
                    <input style={{ width: '40%' }} type="text" placeholder="Search...." name="search" value={q} onChange={(e) => { setQ(e.target.value) }} /> <Button onClick={() => { ProductsList() }}>Search</Button> {/*<Button onClick={() => { clearSearch() }}>Clear</Button>*/}
                </div>
                <div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/addProducts") }}>Add New Product</Button></div>
            </div>
            <div style={{ width: '100%' }}>
                <div style={{ width: '25%', float: 'left', marginLeft: '2%', marginTop: '1%' }}><h2>Product List</h2></div>
                <div style={{ width: '60%', float: 'right', marginTop: '2%', marginLeft: '2%', marginRight: '2%', textAlign: 'right' }}><h5>{resultcount} Results Found</h5></div>
            </div>
            <div className="userlist">
                <h2>&nbsp;</h2>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th style={{width: '5%'}}>#</th>
                            <th style={{width: '40%'}}>Name <button onClick={() => { sortingdata("name", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("name", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button> </th>
                            <th style={{width: '20%'}}>Category {/*<button onClick={() => { sortingdata("last_name", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("last_name", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button>*/}</th>
                            <th style={{width: '14%'}}>Price <button onClick={() => { sortingdata("price", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("price", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button></th>
                            <th style={{width: '14%'}}>Quantity <button onClick={() => { sortingdata("quantity", "1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬆️</button> <button onClick={() => { sortingdata("quantity", "-1") }} style={{ border: '0', backgroundColor: 'white', margin: '0', padding: '0' }}>⬇️</button></th>
                            <th style={{width: '10%'}}>Status</th>
                            <th style={{width: '15%'}}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            !loading ?
                                currentItems.map((userdata, i) =>
                                    <tr key={i}>
                                        <td>{i + 1}</td>
                                        <td>{userdata.name}</td>
                                        <td>{userdata.category && userdata.category !== "" ? userdata.category : "N/A"}</td>
                                        <td>${userdata.price}</td>
                                        <td>{userdata.quantity}</td>
                                        <td>{userdata.status ? "Active" : "Inactive"}</td>
                                        <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                                            {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                                            <Dropdown.Item onClick={() => { history.push("/updateProduct/" + userdata._id) }}>Edit Product</Dropdown.Item>
                                            <Dropdown.Item onClick={() => { removeProduct(userdata._id, userdata.name) }}>Delete Product</Dropdown.Item>
                                            <Dropdown.Item onClick={() => { statusProduct(userdata._id) }}>Change Status</Dropdown.Item>
                                        </DropdownButton></td>
                                    </tr>
                                ) : <tr><td colSpan="7" style={{ paddingTop: '20px', paddingBottom: '20px' }}><Spinner animation="border" role="status">
                                    <span className="visually-hidden">Loading...</span>
                                </Spinner></td></tr>
                        }
                    </tbody>
                </Table>
            </div>
            {
                resultcount && resultcount>0 ?
            <div>
                <div style={{ marginTop: '30px' }}>
                    <ul className="pageNumbers">
                        <li>
                            <button
                                onClick={handlePrevbtn}
                                disabled={currentPage === pages[0] ? true : false}
                            >
                                Prev
                            </button>
                        </li>
                        {pageDecrementBtn}
                        <Pagination size="sm">{renderPageNumbers}</Pagination>
                        {pageIncrementBtn}

                        <li>
                            <button
                                onClick={handleNextbtn}
                                disabled={currentPage === pages[pages.length - 1] ? true : false}
                            >
                                Next
                            </button>
                        </li>
                    </ul>
                    {/*<button onClick={handleLoadMore} className="loadmore">
                    Load More
                </button>*/}
                </div>
            </div> : ""
                }
        </Container>

    )
}
export default Products
