import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
import "./paginationstyle.css";

function UpdateProfile() {
    const history = useHistory();
  const [data, setData] = useState([]);

  


  const removeUser = (user_id, name) => {
    var result = window.confirm("Want to delete the User " + name + "?");
    if (result) {
        axios.delete("http://localhost:9001/user/userdelete/" + user_id).then((res) => {
            if(res.data.message && res.data.message==='user deleted successfully'){
                alert(res.data.message);
                userList()
            }
        })
    }
}
const statusUser = (user_id) => {
    axios.put("http://localhost:9001/user/changeStatus/" + user_id).then((res) => {
        if(res.data.message && res.data.message==='user status updated successfully'){
            alert(res.data.message);
            userList()
        }
    })
}

  

  useEffect(() => {
    userList()
  }, []);

  const userList = () => {
    axios.get("http://localhost:9001/user/userList").then((res) => {
        setData(res.data.userdata);
    })
}

  

  return (
    <>
     
      <Container className="maindivmargin">

<div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>

<div className="userlist">
    <h2>Users List</h2>
    <Table responsive striped bordered hover>
        <thead>
            <tr>
                <th>#</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>User Type</th>
                <th>Status</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            {
                currentItems.map((userdata, i) =>
                    <tr key={i}>
                        <td>{i + 1}</td>
                        <td>{userdata.first_name}</td>
                        <td>{userdata.last_name}</td>
                        <td>{userdata.email}</td>
                        <td>{userdata.phone}</td>
                        <td>{userdata.user_type}</td>
                        <td>{userdata.status ? "Active" : "Inactive"}</td>
                        <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                            {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                            <Dropdown.Item onClick={()=>{history.push("/updateUser/"+userdata._id)}}>Edit User</Dropdown.Item>
                            {
                                JSON.parse(localStorage.getItem("MyUser"))._id && JSON.parse(localStorage.getItem("MyUser"))._id!==userdata._id ?
                                <> <Dropdown.Item onClick={() => { removeUser(userdata._id, userdata.first_name) }}>Delete User</Dropdown.Item>
                                <Dropdown.Item onClick={() => { statusUser(userdata._id) }}>Change Status</Dropdown.Item></>
                                :
                                ""
                            }
                        </DropdownButton></td>
                    </tr>
                )
            }
        </tbody>
    </Table>
</div>
<div>
<ul className="pageNumbers">
        <li>
          <button
            onClick={handlePrevbtn}
            disabled={currentPage === pages[0] ? true : false}
          >
            Prev
          </button>
        </li>
        {pageDecrementBtn}
        {renderPageNumbers}
        {pageIncrementBtn}

        <li>
          <button
            onClick={handleNextbtn}
            disabled={currentPage === pages[pages.length - 1] ? true : false}
          >
            Next
          </button>
        </li>
      </ul>
      <button onClick={handleLoadMore} className="loadmore">
        Load More
      </button>
</div>
</Container>



      
    </>
  );
}

export default UpdateProfile;
