import './App.css';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Page_not_found_404 from './components/Page_not_found_404';
import {Switch, Route } from 'react-router-dom';
import { useEffect, useState } from 'react';
function App() {
  const [logUser, setLogUser] = useState({});
  useEffect(() => {
    setLogUser(JSON.parse(localStorage.getItem("MyUser")));
  })
  const updateUserinlocalStorage = (user) =>{
    localStorage.setItem("MyUser", JSON.stringify(user));
    setLogUser(logUser);
  }
  return (
    <div className="App">
      <Switch>
        <Route path="/register"><Register /></Route>
        <Route path="/login"><Login updateUserinlocalStorage={updateUserinlocalStorage} /></Route>
        <Route path="/" exact={true}><Dashboard updateUserinlocalStorage={updateUserinlocalStorage} /></Route>
        <Route path="*"><Page_not_found_404 /></Route>
      </Switch>
    </div>
  );
}
export default App;
