import React, { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom';
import axios from 'axios';
const Login = (props) => {
    const history = useHistory();
    const [user, setUser] = useState({
        email: "",
        password: ""
    })

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (storedData._id && storedData._id !== "") {
            history.push("/");
        } else {
            history.push("/login");
        }
    }, [])  // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    const loginUser = () => {
        //const {email, password} = user
        axios.post("http://localhost:9001/user/login", user).then((res) => {
            if (res.data.message === "Login Successfully") {
                alert(res.data.message)
                props.updateUserinlocalStorage(res.data.user);
                history.push("/");
            } else {
                alert(res.data.message)
            }
        })
    }
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="register">
                <h1>Login</h1>
                <input type="text" value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />
                <input type="password" value={user.password} placeholder="Password" name="password" onChange={inputVal} />
                <div className="button" onClick={() => { loginUser() }}>Login</div>
                <div>OR</div>
                <div className="button" onClick={() => { history.push("/register") }}>Register {/* OR <Link to="/register">Register</Link> */}</div>
            </div>
        </>
    )
}
export default Login;
