import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom';
import axios from 'axios';
import useValidator from './useValidator'
const Register = () => {
    let myButton = '';
    let disfield = '';
    let { userID } = useParams();
    const history = useHistory();
    const [validator, showValidationMessage] = useValidator()
    const [user, setUser] = useState({
        f_name: "",
        l_name: "",
        email: "",
        phone: "",
        usertype: "",
        password: "",
        confPassword: ""
    })

    useEffect(() => {
        if (userID && userID !== undefined) {
            axios.get("http://localhost:9001/user/userdata/" + userID).then((res) => {
                setUser({
                    f_name: res.data.userdata.first_name,
                    l_name: res.data.userdata.last_name,
                    email: res.data.userdata.email,
                    phone: res.data.userdata.phone,
                    usertype: res.data.userdata.user_type,
                    password: "",
                    confPassword: ""
                })
            })
        }
    }, [userID])

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps
    const inputVal = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        })
    }
    const addUser = () => {
        if (validator.allValid()) {
            //const {f_name, l_name, email, phone, usertype, password, confPassword} = user
            axios.post("http://localhost:9001/user/addUser", user).then((res) => {
                if (res.data.message === 'User already registerd. Please use another email!') {
                    alert(res.data.message);
                } else {
                    alert(res.data.message);
                    history.push("/users");
                }
            })
        } else {
            //validator.showMessages();
            showValidationMessage(true);
            //console.log("Error Message: ", validator.getErrorMessages())
            //forceUpdate();
        }
    }
    const updateUser = () => {
        if (validator.allValid()) {
            axios.put(`http://localhost:9001/user/editUser/${userID}`, user).then((res) => {
                if (res.data.message === 'user updated successfully') {
                    alert(res.data.message);
                    history.push("/users");
                }
            })
        } else {
            //validator.showMessages();
            showValidationMessage(true);
            //console.log("Error Message: ", validator.getErrorMessages())
            //forceUpdate();
        }
    }
    if (userID && userID !== undefined) {
        myButton = <div className="mybutton" onClick={() => { updateUser() }}>Update User</div>
        disfield = 'disabled'
    } else {
        myButton = <div className="mybutton" onClick={() => { addUser() }}>Add User</div>
        disfield = ''
    }
    return (
        <>
            {/* onChange={(e)=>{setUser({...user, f_name: e.target.value})}} */}
            <div className="centerform">
                <div className="register">
                    <h1>{userID && userID !== undefined ? "Update User" : "Add User"}</h1>

                    <div style={{ width: '100%', minHeight:'68px' }}>
                        <div style={{ float: 'left', width: '50%' }}>
                            <input className="myinputclass" type="text" value={user.f_name} placeholder="First Name" name="f_name" onChange={inputVal} />
                            {validator.message('f_name', user.f_name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "First Name is required"
                                }
                            })}
                        </div>
                        <div style={{ float: 'right', width: '50%' }}>
                            <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" value={user.l_name} placeholder="Last Name" name="l_name" onChange={inputVal} />
                            {validator.message('l_name', user.l_name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "Last Name is required"
                                }
                            })}
                        </div>
                    </div>
                    <div style={{ width: '100%', minHeight:'68px' }}>
                        <div style={{ float: 'left', width: '50%' }}>
                            <input className="myinputclass" type="text" disabled={disfield} value={user.email} placeholder="Email Address" name="email" onChange={inputVal} />
                            {userID && userID !== undefined ? "" :
                                validator.message('email', user.email, 'required|email', {
                                    className: 'text-danger mywidth',
                                    messages: {
                                        required: "Email is required"
                                    }
                                })
                            }
                        </div>
                        <div style={{ float: 'right', width: '50%' }}>
                            <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" value={user.phone} placeholder="Phone" name="phone" onChange={inputVal} />
                        </div>
                    </div>
                    <div style={{ width: '100%', minHeight:'68px' }}>
                        <select className="myinputclass" name="usertype" value={user.usertype} onChange={inputVal}>
                            <option value="">
                                Please Select User Type
                            </option>
                            <option value="Super Admin">
                                Super Admin
                            </option>
                            <option value="Admin">
                                Admin
                            </option>
                            <option value="Staff">
                                Staff
                            </option>
                            <option value="Account">
                                Account
                            </option>
                        </select>
                        {validator.message('usertype', user.usertype, 'required', {
                            className: 'text-danger mywidth',
                            messages: {
                                required: "User Type is required"
                            }
                        })}
                    </div>

                    <div style={{ width: '100%', minHeight:'68px' }}>
                        <div style={{ float: 'left', width: '50%' }}>
                            <input className="myinputclass" type="password" value={user.password} placeholder="Password" name="password" onChange={inputVal} />
                            {userID && userID !== undefined ? "" :
                                validator.message('password', user.password, 'required|min:6|max:12', {
                                    className: 'text-danger mywidth',
                                    messages: {
                                        required: "Password is required"
                                    }
                                })
                            }
                        </div>
                        <div style={{ float: 'right', width: '50%' }}>
                            <input className="myinputclass" style={{ marginLeft: "15px" }} type="password" value={user.confPassword} placeholder="Confirm Password" name="confPassword" onChange={inputVal} />
                            {userID && userID !== undefined ? "" :
                                validator.message('confPassword', user.confPassword, `required|min:6|max:12|in:${user.password}`, {
                                    className: 'text-danger mywidth',
                                    messages: {
                                        required: "Confirm Password is required",
                                        in: "Password and Confirm Password need to match"
                                    }
                                })
                            }
                        </div>
                    </div>
                    <div style={{ width: '100%', marginTop:'3%' }}>
                    {myButton}
                    </div>
                    <div>OR</div>
                    <div className="button" onClick={() => { history.push("/users") }}>Back </div>
                    {/*<div>OR</div>
                    <div className="button" onClick={() => { history.push("/login") }}>Login </div>*/}
                    {/* OR <Link to="/login">Login</Link> */}
                </div>
            </div>
        </>
    )
}
export default Register;
