import React, { useEffect, useState } from 'react';
import { Table, Container, DropdownButton, Dropdown, Button } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
//import "./style.css";
const renderData = (data) => {
  return (
    <ul>
      {data.map((todo, index) => {
        return <li key={index}>{todo.first_name}</li>;
      })}
    </ul>
  );
};

function UpdateProfile() {
    const history = useHistory();
  const [data, setData] = useState([]);

  const [currentPage, setcurrentPage] = useState(1);
  const [itemsPerPage, setitemsPerPage] = useState(5);

  const [pageNumberLimit, setpageNumberLimit] = useState(5);
  const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
  const [minPageNumberLimit, setminPageNumberLimit] = useState(0);

  const handleClick = (event) => {
    setcurrentPage(Number(event.target.id));
  };

  const pages = [];
  for (let i = 1; i <= Math.ceil(data.length / itemsPerPage); i++) {
    pages.push(i);
  }

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

  const renderPageNumbers = pages.map((number) => {
    if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
      return (
        <li
          key={number}
          id={number}
          onClick={handleClick}
          className={currentPage == number ? "active" : null}
        >
          {number}
        </li>
      );
    } else {
      return null;
    }
  });

  useEffect(() => {
    fetch("http://localhost:9001/user/userList")
      .then((response) => response.json())
      .then((json) => setData(json.userdata));
  }, []);

  const handleNextbtn = () => {
    setcurrentPage(currentPage + 1);

    if (currentPage + 1 > maxPageNumberLimit) {
      setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
      setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
    }
  };

  const handlePrevbtn = () => {
    setcurrentPage(currentPage - 1);

    if ((currentPage - 1) % pageNumberLimit == 0) {
      setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
      setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
    }
  };

  let pageIncrementBtn = null;
  if (pages.length > maxPageNumberLimit) {
    pageIncrementBtn = <li onClick={handleNextbtn}> &hellip; </li>;
  }

  let pageDecrementBtn = null;
  if (minPageNumberLimit >= 1) {
    pageDecrementBtn = <li onClick={handlePrevbtn}> &hellip; </li>;
  }

  const handleLoadMore = () => {
    setitemsPerPage(itemsPerPage + 5);
  };

  return (
    <>
     
      <Container className="maindivmargin">

<div style={{ textAlign: 'right', marginBottom: '14px', marginRight: '5px' }}><Button onClick={() => { history.push("/register") }}>Add New User</Button></div>

<div className="userlist">
    <h2>Users List</h2>
    <Table responsive striped bordered hover>
        <thead>
            <tr>
                <th>#</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>User Type</th>
                <th>Status</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            {
                currentItems.map((userdata, i) =>
                    <tr key={i}>
                        <td>{i + 1}</td>
                        <td>{userdata.first_name}</td>
                        <td>{userdata.last_name}</td>
                        <td>{userdata.email}</td>
                        <td>{userdata.phone}</td>
                        <td>{userdata.user_type}</td>
                        <td>{userdata.status ? "Active" : "Inactive"}</td>
                        <td><DropdownButton key="Primary" title="Actions" id="dropdown-variants-Primary">
                            {/*<Dropdown.Item onClick={() => { props.updateUser(userdata._id) }}>Edit User</Dropdown.Item>*/}
                            <Dropdown.Item onClick={()=>{history.push("/updateUser/"+userdata._id)}}>Edit User</Dropdown.Item>
                            {
                                JSON.parse(localStorage.getItem("MyUser"))._id && JSON.parse(localStorage.getItem("MyUser"))._id!==userdata._id ?
                                <> <Dropdown.Item onClick={() => { removeUser(userdata._id, userdata.first_name) }}>Delete User</Dropdown.Item>
                                <Dropdown.Item onClick={() => { statusUser(userdata._id) }}>Change Status</Dropdown.Item></>
                                :
                                ""
                            }
                        </DropdownButton></td>
                    </tr>
                )
            }
        </tbody>
    </Table>
</div>
</Container>



      <ul className="pageNumbers">
        <li>
          <button
            onClick={handlePrevbtn}
            disabled={currentPage == pages[0] ? true : false}
          >
            Prev
          </button>
        </li>
        {pageDecrementBtn}
        {renderPageNumbers}
        {pageIncrementBtn}

        <li>
          <button
            onClick={handleNextbtn}
            disabled={currentPage == pages[pages.length - 1] ? true : false}
          >
            Next
          </button>
        </li>
      </ul>
      <button onClick={handleLoadMore} className="loadmore">
        Load More
      </button>
    </>
  );
}

export default UpdateProfile;
