import './App.css';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Page_not_found_404 from './components/Page_not_found_404';
import { Switch, Route, useHistory } from 'react-router-dom';
import { useEffect, useState } from 'react';
function App() {
  const [logUser, setLogUser] = useState({});
  const history = useHistory();
  useEffect(() => {
    setLogUser(JSON.parse(localStorage.getItem("MyUser")));
  }, [])
  const UpdateUserinlocalStorage = (user) => {
    localStorage.setItem("MyUser", JSON.stringify(user));
    useEffect(() => {
      setLogUser(user);
    }, [])

  }
  return (
    <div className="App">
      <Switch>
        <Route path="/register">
          {
            logUser && logUser._id ? history.push("/") : <Register />
          }
        </Route>
        <Route path="/login">
          {
            logUser && logUser._id ? history.push("/") : <Login updateUserinlocalStorage={updateUserinlocalStorage} />
          }
        </Route>
        <Route path="/" exact={true}>
          {
            logUser && logUser._id ? <Dashboard updateUserinlocalStorage={updateUserinlocalStorage} loggedinuser ={logUser} /> : <Login updateUserinlocalStorage={updateUserinlocalStorage} />
          }
        </Route>
        <Route path="*"><Page_not_found_404 /></Route>
      </Switch>
    </div>
  );
}
export default App;
