import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom';
import useValidator from './useValidator'
import { Form } from 'react-bootstrap'
import { getCategoryList } from '../Services/categoryapi'
import { addProduct, editsingleProduct, getsingleProduct, addImages } from '../Services/productapi'
//import NavbarCollapse from 'react-bootstrap/esm/NavbarCollapse';
import Productimages from './Productimages'
const AddProduct = (props) => {
    let myButton = '';
    let { prodID } = useParams();
    const history = useHistory();
    const [validator, showValidationMessage] = useValidator()
    const [subcatdata, setSubcatdata] = useState([]);
    const [uploadPercentage, setUploadPercentage] = useState(0)
    const [product, setProduct] = useState({
        name: "",
        quantity: "",
        price: "",
        categoryID: "",
        discount: "",
        description: "",
        productPicture: [],
        productPictureviewonly : "",
        imageuploaded: 'false',
        createdBy: ""
    })

    // set the props as a default state in createdBy state
    useEffect(() => {
        if (props.loggedinuser._id) {
            setProduct({...product, createdBy : props.loggedinuser._id});
        }
    }, [props.loggedinuser._id]) // eslint-disable-line react-hooks/exhaustive-deps

    const handlePhoto = async (e) => {
        let formData = new FormData();

        const options = {
            onUploadProgress: (progressEvent) => {
              const {loaded, total} = progressEvent;
              let percent = Math.floor( (loaded * 100) / total )
              console.log( `${loaded}kb of ${total}kb | ${percent}%` );
      
              if( percent < 100 ){
                this.setState({ uploadPercentage: percent })
              }
            }
          }

        //let productPicturelatest = []
        Array.from(e.target.files)
            .forEach(file => {
                formData.append('files', file);
                ///productPicturelatest.push(file);
                //console.log(new Date().getTime() + '-' + file.name)
            });
        let response = await addImages(formData, options);
        //console.log("AABBCC", response.data)
        setProduct({ ...product, imageuploaded: 'true', productPicture: response.data });
        //setProduct({ ...product, productPicture: productPicturelatest });
    }

    //console.log("list data", subcatdata)
    useEffect(() => {
        async function getproddata() {
            if (prodID && prodID !== undefined) {
                //axios.get("http://localhost:9001/user/userdata/" + catID).then((res) => {
                let res = await getsingleProduct(prodID);
                setProduct({
                    name: res.data.productdata.name,
                    quantity: res.data.productdata.quantity,
                    price: res.data.productdata.price,
                    categoryID: res.data.productdata.categoryID,
                    discount: res.data.productdata.discount,
                    description: res.data.productdata.description,
                    productPicture: res.data.productdata.productPictures,
                    productPictureviewonly: res.data.productdata.productPictures,
                    imageuploaded: 'false',
                    createdBy: ""
                })
            }
        }
        getproddata()
    }, [prodID]) // eslint-disable-line react-hooks/exhaustive-deps

    //console.log(product);

    useEffect(() => {
        document.title = "Add Products";
        const storedData = JSON.parse(localStorage.getItem("MyUser"));
        if (!storedData._id) {
            history.push("/");
        }
        // Getting the Subcategory dropdown data
        const getSubcatdataList = async () => {
            let res = await getCategoryList();
            setSubcatdata(res.data.catdata)
        }
        getSubcatdataList()
    }, [])   // eslint-disable-line react-hooks/exhaustive-deps

    const inputVal = (e) => {
        const { name, value } = e.target;
        setProduct({
            ...product,
            [name]: value
        })
    }
    const adddProduct = async () => {
        if (validator.allValid()) {
            const formData = new FormData();
            formData.append('name', product.name)
            formData.append('quantity', product.quantity)
            formData.append('price', product.price)
            formData.append('categoryID', product.categoryID)
            formData.append('discount', product.discount)
            formData.append('description', product.description)
            formData.append('imageuploaded', product.imageuploaded)
            formData.append('productPicture', product.productPicture)
            formData.append('createdBy', product.createdBy)
            let response = await addProduct(formData);
            if (response.data.message === 'Product created Successfully.') {
                setProduct({ ...product, imageuploaded: 'false' });
                alert(response.data.message);
                history.push("/products");
            } else {
                alert(response.data.message);
            }
        } else {
            showValidationMessage(true);
        }
    }
    //axios.put(`http://localhost:9001/user/editUser/${catID}`, user).then((res) => { })
    const updateProduct = async () => {
        if (validator.allValid()) {
            const formData = new FormData();
            formData.append('name', product.name)
            formData.append('quantity', product.quantity)
            formData.append('price', product.price)
            formData.append('categoryID', product.categoryID)
            formData.append('discount', product.discount)
            formData.append('description', product.description)
            formData.append('imageuploaded', product.imageuploaded)
            formData.append('productPicture', product.productPicture)
            formData.append('createdBy', product.createdBy)
            let response = await editsingleProduct(prodID, formData);
            if (response.data.message === 'Product updated successfully') {
                setProduct({ ...product, imageuploaded: 'false' });
                alert(response.data.message);
                history.push("/products");
            } else {
                alert(response.data.message);
            }
        } else {
            showValidationMessage(true);
        }
    }
    if (prodID && prodID !== undefined) {
        myButton = <div className="mybutton" onClick={() => { updateProduct() }}>Update Product</div>
    } else {
        myButton = <div className="mybutton" onClick={() => { adddProduct() }}>Add Product</div>
    }

    const createCategoryList = (categories, options = [], sub_mark = '') => {
        for (let category of categories) {
            options.push({ value: category._id, name: sub_mark + category.name, children: category.parentID });
            if (category.children.length > 0) {
                createCategoryList(category.children, options, sub_mark + '---')
            }
        }
        return options;
    }
    return (
        <>
            {/* onChange={(e)=>{setProduct({...product, f_name: e.target.value})}} */}
            <div className="centerform">
                <div className="register">
                    <h1>{prodID && prodID !== undefined ? "Update Product" : "Add Product"}</h1>

                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <div style={{ float: 'left', width: '50%' }}>
                            <input className="myinputclass" type="text" value={product.name} placeholder="Product Name" name="name" onChange={inputVal} />
                            {validator.message('name', product.name, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "Product Name is required"
                                }
                            })}
                        </div>
                        <div style={{ float: 'right', width: '50%' }}>
                            <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" value={product.quantity} placeholder="Quantity" name="quantity" onChange={inputVal} />
                            {validator.message('quantity', product.quantity, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "Quantity is required"
                                }
                            })}
                        </div>
                    </div>

                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <div style={{ float: 'left', width: '50%' }}>
                            <input className="myinputclass" type="text" value={product.price} placeholder="Product Price" name="price" onChange={inputVal} />
                            {validator.message('price', product.price, 'required', {
                                className: 'text-danger mywidth',
                                messages: {
                                    required: "Product Price is required"
                                }
                            })}
                        </div>
                        <div style={{ float: 'right', width: '50%' }}>
                            <input className="myinputclass" style={{ marginLeft: "15px" }} type="text" value={product.discount} placeholder="Discount in %" name="discount" onChange={inputVal} />
                        </div>
                    </div>

                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <select style={{ width: '96%' }} className="myinputclass" name="categoryID" value={product.categoryID} onChange={inputVal}>
                            <option value="">Please Select Category</option>
                            {
                                createCategoryList(subcatdata).map(option =>
                                    <option key={option.value} value={option.value}>{option.name}</option>)
                            }
                        </select>
                        {validator.message('categoryID', product.categoryID, 'required', {
                            className: 'text-danger mywidth',
                            messages: {
                                required: "Category is required"
                            }
                        })}
                    </div>

                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                            <Form.Control as="textarea" rows={3} name="description" value={product.description} onChange={inputVal} />
                        </Form.Group>
                    </div>

                    <div style={{ width: '100%', minHeight: '76px' }}>
                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Label>Product Image: &nbsp;<br />(Accept only .png, .jpg, .jpeg)&nbsp;&nbsp;</Form.Label>
                            <Form.Control type="file" multiple name="productPicture" onChange={handlePhoto} accept=".png, .jpg, .jpeg" />
                        </Form.Group><br />
                        
                        <Productimages prodID={prodID} product={product} />


                    </div>

                    <div style={{ width: '100%', marginTop: '3%' }}>
                        {myButton}
                    </div>
                    <div>OR</div>
                    <div className="button" onClick={() => { history.push("/products") }}>Back </div>
                    {/*<div>OR</div>
                    <div className="button" onClick={() => { history.push("/login") }}>Login </div>*/}
                    {/* OR <Link to="/login">Login</Link> */}
                </div>
            </div>
        </>
    )
}
export default AddProduct;
